/* ==UserStyle==
@name Stylus DeepDark
@namespace github.com/RaitaroH/Stylus-DeepDark
@homepageURL https://github.com/RaitaroH/Stylus-DeepDark
@version 1.2.2
@updateURL https://raw.githubusercontent.com/RaitaroH/Stylus-DeepDark/master/StylusDeepDark.user.css
@description Stylus dark theme
@author RaitaroH
@license GNU-V3.0

@preprocessor uso
@var select mainColor 'Main color' {
	"DeepDark": "#00adee",
	"BreezeDark": "#3DAEE9",
	"Vertex Dark": "#4080fb",
	"Arc Dark": "#5294E2",
	"Firefox Dark": "#5675B9",
	"Firefox57": "#4080FB",
	"Discord": "#7289DA",
	"YouTubeDark": "#E52117",
	"Mint-Y-Dark": "#9AB87C",
	"9anime": "#723f8c",
	"Black&White": "#ffffff",
	"Yellow_petrocompletions": "#FFC700",
	"Yellow2": "#FFC700",
	"Ubuntu_grey": "#EF7847",
	"Ubuntu_purple": "#EF7847",
	"Orange": "#ff6905",
	"Jisho_夜明け":  "#EF7D6C"
}
@var select mainBackground 'Main background color' {
	"DeepDark": "#111111",
	"BreezeDark": "#232629",
	"Vertex Dark": "#2B2B2C",
	"Arc Dark": "#343944",
	"Firefox Dark": "#272B35",
	"Firefox57": "#0C0C0D",
	"Discord": "#1E2124",
	"YouTubeDark": "#111111",
	"Mint-Y-Dark": "#2F2F2F",
	"9anime": "#0B0A0D",
	"Black&White": "#000000",
	"Yellow_petrocompletions": "#141414",
	"Yellow2": "#0a0800",
	"Ubuntu_grey": "#312D2A",
	"Ubuntu_purple": "#2C071A",
	"Orange": "#0a0400",
	"Jisho_夜明け":  "#332222"
}
@var select secondBackground 'Second background color' {
	"DeepDark": "#181818",
	"BreezeDark": "#2a2e32",
	"Vertex Dark": "#353638",
	"Arc Dark": "#383C4A",
	"Firefox Dark": "#181D20",
	"Firefox57": "#252526",
	"Discord": "#2F3136",
	"YouTubeDark": "#232323",
	"Mint-Y-Dark": "#383838",
	"9anime": "#17151C",
	"Black&White": "#0a0a0a",
	"Yellow_petrocompletions": "#202222",
	"Yellow2": "#0c0a04",
	"Ubuntu_grey": "#3D3C38",
	"Ubuntu_purple": "#430B28",
	"Orange": "#0e0702",
	"Jisho_夜明け":  "#2A1B1B"
}
@var select hoverBackground 'Hover background color' {
	"DeepDark": "#232323",
	"BreezeDark": "#31363b",
	"Vertex Dark": "#515254",
	"Arc Dark": "#414A59",
	"Firefox Dark": "#353A44",
	"Firefox57": "#323234",
	"Discord": "#484B51",
	"YouTubeDark": "#343434",
	"Mint-Y-Dark": "#404040",
	"9anime": "#1E1C25",
	"Black&White": "#0f0f0f",
	"Yellow_petrocompletions": "#353838",
	"Yellow2": "#0f0d05",
	"Ubuntu_grey": "#59564D",
	"Ubuntu_purple": "#520D30",
	"Orange": "#110903",
	"Jisho_夜明け":  "#863B2F"
}
@var select mainText 'Main text color' {
	"DeepDark": "#eff0f1",
	"BreezeDark": "#eff0f1",
	"Vertex Dark": "#F3F3F5",
	"Arc Dark": "#C1C8D1",
	"Firefox Dark": "#E3EEF9",
	"Firefox57": "#F9F9FA",
	"Discord": "#FFFFFF",
	"YouTubeDark": "#E1E1E1",
	"Mint-Y-Dark": "#FFFFFF",
	"9anime": "#B4B4B4",
	"Black&White": "#b4b4b4",
	"Yellow_petrocompletions": "#EFF0F1",
	"Yellow2": "#fffdf5",
	"Ubuntu_grey": "#F2F1EF",
	"Ubuntu_purple": "#F2F1EF",
	"Orange": "#fff9f5",
	"Jisho_夜明け":  "#EFB26C"
}
@var select dimerText 'Secondary text color' {
	"DeepDark": "#CCCCCC",
	"BreezeDark": "#bdc3c7",
	"Vertex Dark": "#AEAFB0",
	"Arc Dark": "#B3BAC5",
	"Firefox Dark": "#606376",
	"Firefox57": "#D0D0D0",
	"Discord": "#ADA8AA",
	"YouTubeDark": "#7F7F7F",
	"Mint-Y-Dark": "#D5DADA",
	"9anime": "#747474",
	"Black&White": "#828282",
	"Yellow_petrocompletions": "#9F9999",
	"Yellow2": "#fff8e1",
	"Ubuntu_grey": "#E6E5E3",
	"Ubuntu_purple": "#E6E5E3",
	"Orange": "#ffede1",
	"Jisho_夜明け":  "#986E3F"
}

==/UserStyle== */
@-moz-document regexp("moz-extension://.*"), regexp("chrome-extension://.*") {

/*Theme made by RaitaroH @https://github.com/RaitaroH/Stylus-DeepDark*/

/*GNU General Public License v3.0*/

/*1.2.2*/

	/*Main color variables*/
	:root
	{
		/*User colors*/
		--main-color: /*[[mainColor]]*/;
		--main-background: /*[[mainBackground]]*/;
		--second-background: /*[[secondBackground]]*/;
		--hover-background: /*[[hoverBackground]]*/;
		--main-text: /*[[mainText]]*/;
		--dimer-text: /*[[dimerText]]*/;

		--shadow: 0 1px 0.5px rgba(0,0,0,0.13);

		/*DeepDark colors*/
		/*
		--main-color: #00adee;
		--main-background: #111111;
		--second-background: #181818;
		--hover-background: #232323;
		--main-text: #eff0f1;
		--dimer-text: #CCCCCC;
		*/

		/*KDE colors*/
		/*
		--main-color: #3DAEE9;
		--main-background: #232629;
		--second-background: #2a2e32;
		--hover-background: #31363b;
		--main-text: #eff0f1;
		--dimer-text: #bdc3c7;
		*/

		/*Vertex Dark colors*/
		/*
		--main-color: #4080fb;
		--main-background: #2B2B2C;
		--second-background: #353638;
		--hover-background: #515254;
		--main-text: #F3F3F5;
		--dimer-text: #AEAFB0;
		*/

		/*Arc Dark colors*/
		/*
		--main-color: #5294E2;
		--main-background: #343944;
		--second-background: #383C4A;
		--hover-background: #414A59;
		--main-text: #C1C8D1;
		--dimer-text: #B3BAC5;
		*/

		/*Firefox Dark colors*/
		/*
		--main-color: #5675B9;
		--main-background: #272B35;
		--second-background: #181D20;
		--hover-background: #353A44;
		--main-text: #E3EEF9;
		--dimer-text: #606376;
		*/

		/*Firefox57 Dark colors*/
		/*
		--main-color: #4080FB;
		--main-background: #0C0C0D;
		--second-background: #252526;
		--hover-background: #323234;
		--main-text: #F9F9FA;
		--dimer-text: #D0D0D0;
		*/

		/*Discord colors*/
		/*
		--main-color: #7289DA;
		--main-background: #1E2124;
		--second-background: #2F3136;
		--hover-background: #484B51;
		--main-text: #FFFFFF;
		--dimer-text: #ADA8AA;
		*/

		/*YouTube dark colors*/
		/*
		--main-color: #E52117;
		--main-background: #111111;
		--second-background: #232323;
		--hover-background: #343434;
		--main-text: #E1E1E1;
		--dimer-text: #7F7F7F;
		*/

		/*Mint-Y-Dark colors*/
		/*
		--main-color: #9AB87C;
		--main-background: #2F2F2F;
		--second-background: #383838;
		--hover-background: #404040;
		--main-text: #FFFFFF;
		--dimer-text: #D5DADA;
		*/

		/*9anime colors*/
		/*
		--main-color: #723f8c;
		--main-background: #0B0A0D;
		--second-background: #17151C;
		--hover-background: #1E1C25;
		--main-text: #B4B4B4;
		--dimer-text: #747474;
		*/

		/*Black and white*/
		/*
		--main-color: #ffffff;
		--main-background: #000000;
		--second-background: #0a0a0a;
		--hover-background: #0f0f0f;
		--main-text: #b4b4b4;
		--dimer-text: #828282;
		*/

		/*Yellow (colors from petrocompletions)*/
		/*
		--main-color: #FFC700;
		--main-background: #141414;
		--second-background: #202222;
		--hover-background: #353838;
		--main-text: #EFF0F1;
		--dimer-text: #9F9999;
		*/

		/*Yellow 2*/
		/*
		--main-color: #ffc700;
		--main-background: #0a0800;
		--second-background: #0c0a04;
		--hover-background: #0f0d05;
		--main-text: #fffdf5;
		--dimer-text: #fff8e1;
		*/

		/*Ubuntu grey colors*/
		/*
		--main-color: #EF7847;
		--main-background: #312D2A;
		--second-background: #3D3C38;
		--hover-background: #59564D;
		--main-text: #F2F1EF;
		--dimer-text: #E6E5E3;
		*/

		/*Ubuntu purple colors*/
		/*
		--main-color: #EF7847;
		--main-background: #2C071A;
		--second-background: #430B28;
		--hover-background: #520D30;
		--main-text: #F2F1EF;
		--dimer-text: #E6E5E3;
		*/

		/*Orange*/
		/*
		--main-color: #ff6905;
		--main-background: #0a0400;
		--second-background: #0e0702;
		--hover-background: #110903;
		--main-text: #fff9f5;
		--dimer-text: #ffede1;
		*/

		/*Jisho 夜明け colors https://userstyles.org/styles/115621/jisho*/
		/*
		--main-color: #EF7D6C;
		--main-background: #332222;
		--second-background: #2A1B1B;
		--hover-background: #863B2F;
		--main-text: #EFB26C;
		--dimer-text: #986E3F;
		*/
	}

	#stylus-edit, body, html, #notes, #header, .CodeMirror, #actions
	{
		background-color: var(--main-background) !important;
		color: var(--dimer-text) !important;
	}
	a, a:visited, .style-name-link
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	a:hover, .style-name-link:hover
	{
		opacity: 1;
	}

	.disabled h2::after, .disabled h2::after, .entry.usercss .style-name-link::after, .search-result, .search-result-meta
	{
		background-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
	}
	.entry.usercss .style-name-link::after
	{
		color: var(--main-color) !important;
	}

	#filters label:hover, #filters .filter-selection:hover, #filters label:hover select, #filters .filter-selection:hover select
	{
		background-color: transparent !important;
		color: var(--main-color) !important;
	}
	#filters label:hover .svg-icon.select-arrow, #filters .filter-selection:hover .svg-icon.select-arrow
	{
		fill: var(--main-color) !important;
	}
	select, .svg-icon.select-arrow
	{
		transition: none !important;
	}

	.entry
	{
		border-color: var(--hover-background) !important;
	}
	.newUI .entry .style-name:hover::before
	{
		filter: grayscale(1);
	}

	#header h1
	{
		color: var(--main-text) !important;
	}
	#header
	{
		border-color: var(--main-background) !important;
	}
	#header summary h2:hover
	{
		color: var(--main-color) !important;
	}
	#header summary h2:hover
	{
		color: var(--main-color) !important;
	}

	.resize-grip
	{
		background-color: var(--hover-background) !important;
	}
	.CodeMirror
	{
		border-color: var(--hover-background)  !important;
	}


	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}


	/*To be commented out in case you want a stylus theme*/
	.CodeMirror-line::-moz-selection, .CodeMirror-line > span::-moz-selection, .CodeMirror-line > span > span::-moz-selection, .CodeMirror-selected, .CodeMirror-focused .CodeMirror-selected
	{
		background-color: var(--hover-background) !important;
	}
	.CodeMirror
	{
		color: var(--main-text) !important;
	}
	.CodeMirror-lines, .cm-s-mbo.CodeMirror
	{
		background-color: var(--second-background) !important;
	}

	.CodeMirror-activeline-background, .CodeMirror-gutters
	{
		background-color: var(--hover-background) !important;
	}
	.CodeMirror-matchingbracket
	{
		color: var(--main-color) !important;
	}
	.CodeMirror div.CodeMirror-cursor
	{
		border-color: var(--main-color);
	}


	/*Popup*/
	.main-controls, .entry, .firefox body, #help-popup .title
	{
		background-color: var(--main-background) !important;
	}
	#help-popup
	{
		background-color: var(--second-background) !important;
	}
	#confirm > div
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}


	/*Icons*/
	.svg-icon, .svg-icon.info, .newUI .entry .svg-icon
	{
		fill: var(--main-text) !important;
	}
	.svg-icon.info:hover, .svg-icon:hover, a:hover .svg-icon, .newUI .entry:hover .svg-icon:hover
	{
		fill: var(--main-color) !important;
	}


	/*History*/
	#message-box-title, #message-box-buttons
	{
		background-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
	}
	#message-box-contents
	{
		background-color: var(--second-background) !important;
	}

	.help-text mark
	{
		background: var(--hover-background) !important;
	}

	/*Update warning*/
	.newUI .updater-icons > :not(.check-update)::after
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}

	#filters.active, .active #filters-stats
	{
		background-color: var(--main-color) !important;
		border-color: var(--main-color) !important;
	}

	body.all-styles-hidden-by-filters::before, body.all-styles-hidden-by-filters::after
	{
		color: var(--main-color) !important;
		opacity: .5;
	}

	/*List of matching opened tabs*/
	.regexp-report details[data-type="full"]
	{
		color: var(--main-color) !important;
	}


	/*Text fields*/
	#name, .applies-to li .applies-value, #search, .keymap-list input, .config-value
	{
		background-color: var(--hover-background) !important;
		border: 1px solid var(--hover-background) !important;
	}
	input:not([type]), #search
	{
		color: var(--main-text) !important;
	}
	#name:hover, .applies-to li .applies-value:hover, #search:hover, .keymap-list input:hover, #name:active, .applies-to li .applies-value:active, #search:active, .keymap-list input:active, #name:focus, .applies-to li .applies-value:focus, #search:focus, .keymap-list input:focus
	{
		border: 1px solid var(--main-color) !important;
	}


	/*Buttons*/
	#save-button, #beautify, #cancel-button, #from-mozilla, #to-mozilla, #editor\.keyMap, #editor\.theme, #editor\.matchHighlight, #editor\.tabSize, *[class="applies-type style-contributor"], .add-section, .beautify-section, .test-regexp, .remove-applies-to, .add-applies-to, #check-all-updates, #add-style-label, #manage-options-button, button, #file-all-styles, #unfile-all-styles, #manage\.newUI\.targets, #badgeNormal, #badgeDisabled, #updateInterval, #popupWidth, #editor\.linter, #options button, #manage\.onlyUsercss\.invert
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
		border: 1px solid transparent !important;
		box-shadow: var(--shadow) !important;
	}
	#options button
	{
		background-image: none !important;
		text-shadow: none !important;
	}
	#options button:hover
	{
		color: var(--main-color) !important;
	}
	option
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	#save-button:hover, #beautify:hover, #cancel-button:hover, #from-mozilla:hover, #to-mozilla:hover, #editor\.keyMap:hover, #editor\.theme:hover, #editor\.matchHighlight:hover, #editor\.tabSize:hover, *[class="applies-type style-contributor"]:hover, .add-section:hover, .beautify-section:hover, .test-regexp:hover, .remove-applies-to:hover, .add-applies-to:hover, option:hover, #check-all-updates:hover, #add-style-label:hover, #manage-options-button:hover, button:hover, #file-all-styles:hover, #unfile-all-styles:hover, #manage\.newUI\.targets:hover, #updateInterval:hover, #popupWidth:hover, #editor\.linter:hover
	{
		color: var(--main-color) !important;
	}


	.CodeMirror-lint-tooltip
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}


	/*Search*/
	.CodeMirror-search-match, .CodeMirror-search /*, .cm-searching*/
	{
		background: var(--main-color) !important;
		border-color: var(--main-color) !important;
	}
	.CodeMirror-dialog, .CodeMirror-dialog input
	{
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.CodeMirror-dialog-top
	{
		border-color: var(--main-color) !important;
	}


	/*Autocomplete hints*/
	.CodeMirror-hints
	{
		background: var(--main-background) !important;
		box-shadow: var(--shadow) !important;
		border-color: var(--hover-background) !important;
	}
	.CodeMirror-hint
	{
		color: var(--main-text) !important;
	}
	li.CodeMirror-hint-active
	{
		background: var(--main-color) !important;
		border-color: var(--main-color) !important;
	}
	li.CodeMirror-hint:hover
	{
		background: var(--hover-background) !important;
		color: var(--main-color) !important;
	}


	/*Default theme overwritten with adjusted colors*/
	.cm-s-default.CodeMirror { background: var(--second-background); color: var(--main-text); }
	.cm-s-default div.CodeMirror-selected { background: var(--hover-background); }
	.cm-s-default .CodeMirror-line::selection, .cm-s-default .CodeMirror-line > span::selection, .cm-s-default .CodeMirror-line > span > span::selection { background: var(--hover-background); }
	.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-default .CodeMirror-line > span::-moz-selection, .cm-s-default .CodeMirror-line > span > span::-moz-selection { background-color: var(--hover-background); }
	.cm-s-default .CodeMirror-gutters { background: var(--hover-background); border-right: 0px; }
	.cm-s-default .CodeMirror-guttermarker { color: var(--main-text); }
	.cm-s-default .CodeMirror-guttermarker-subtle, .cm-s-default .CodeMirror-linenumber { color: var(--dimer-text); }
	.cm-s-default .CodeMirror-cursor { border-left: 1px solid var(--main-color); }

	.cm-s-default span.cm-comment { color: var(--dimer-text); }
	.cm-s-default span.cm-atom, .cm-s-default span.cm-number { color: var(--main-color); opacity: .6; }

	.cm-s-default span.cm-property, .cm-s-default span.cm-attribute { color: var(--main-color); opacity: 1; }
	.cm-s-default span.cm-keyword, .cm-s-default span.cm-string { color: var(--main-color); }
	.cm-s-default span.cm-string.cm-property { color: var(--main-text); }

	.cm-s-default span.cm-variable { color: var(--main-text); }
	.cm-s-default span.cm-variable-2 { color: var(--main-color); opacity: .6; }
	.cm-s-default .cm-variable-3, .cm-s-default .cm-type { color: var(--main-color); filter: hue-rotate(280deg); }
	.cm-s-default span.cm-def { color: var(--main-text); }
	.cm-s-default span.cm-bracket { color: var(--main-text); font-weight: bold; }
	.cm-s-default span.cm-tag { color: var(--main-color); opacity: .6; }
	.cm-s-default span.cm-link, .cm-s-default span.cm-keyword { color: var(--main-color); }
	.cm-s-default span.cm-keyword:not(.cm-colorview), .cm-s-default span.cm-string { color: var(--main-color); filter: hue-rotate(60deg); }
	.cm-s-default span.cm-link, .cm-s-default span.cm-keyword:not(.cm-colorview) { color: var(--main-color); filter: invert(100%); }
	.cm-s-default span.cm-error { border-bottom: var(--hover-background); color: var(--main-text); }
	.cm-s-default span.cm-qualifier { color: var(--main-text); }

	.cm-s-default .CodeMirror-activeline-background { background: var(--second-background); }
	.cm-s-default .CodeMirror-matchingbracket { color: var(--main-color); }
	.cm-s-default .CodeMirror-matchingtag { background: var(--dimer-text); }

	.cm-s-default .cm-builtin { color: var(--main-color); }
	.cm-s-default .cm-meta { color: var(--dimer-text); }


	/*Stylish in chrome specific*/
	:focus
	{
		outline-color: var(--main-color);
	}
	input
	{
		background-color: var(--second-background);
		color: var(--main-text);
	}
	fieldset, [style-id], .disabled
	{
		border-color: var(--hover-background);
	}
	.CodeMirror.cm-s-default.CodeMirror-wrap.CodeMirror-active
	{
		outline-color: var(--main-color);
	}

	/*Stylus in chrome specific*/
	#manage\.onlyEnabled\.invert, #manage\.onlyLocal\.invert, #manage\.onlyUsercss\.invert
	{
		color: var(--dimer-text);
		box-shadow: var(--shadow) !important;
	}
	#advanced.collapsible h1
	{
		color: var(--main-text) !important;
	}
	#advanced.collapsible h1:hover
	{
		color: var(--main-color) !important;
	}
