	.cm-s-default span.cm-link, .cm-s-default span.cm-keyword:not(.cm-colorview) { color: var(--main-color); filter: invert(100%); }
	.cm-s-default span.cm-error { border-bottom: var(--hover-background); color: var(--main-text); }
	.cm-s-default span.cm-qualifier { color: var(--main-text); }

	.cm-s-default .CodeMirror-activeline-background { background: var(--second-background); }
	.cm-s-default .CodeMirror-matchingbracket { color: var(--main-color); }
	.cm-s-default .CodeMirror-matchingtag { background: var(--dimer-text); }

	.cm-s-default .cm-builtin { color: var(--main-color); }
	.cm-s-default .cm-meta { color: var(--dimer-text); }


	/*Stylish in chrome specific*/
	:focus
