		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.applies-type
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		border: 1px solid var(--hover-background) !important;
	}
	.applies-type:hover
	{
		color: var(--main-color) !important;
	}
	.config-dialog label
	{
		border-color: var(--hover-background) !important;
	}
	.config-dialog select,
	.beautify-options select
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		border: 1px solid var(--hover-background) !important;
	}
	.config-dialog select:hover,
	.beautify-options select:hover
	{
		color: var(--main-color) !important;
	}
	.header
	{
		border-color: var(--hover-background) !important;
	}
	.CodeMirror-focused
	{
		outline-color: var(--main-color) !important;
	}


	/*Checkboxes*/
	input[type="checkbox"], input[type="checkbox"]:not(.slider)
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
	}
	input[type="checkbox"]:hover, input[type="checkbox"]:not(.slider):hover
	{
		border-color: var(--main-color) !important;
	}
	svg[class="svg-icon checked"], input[type="checkbox"]:checked:not(.slider) + .svg-icon.checked
	{
		fill: var(--main-color) !important;
	}

