	.yt-new-promo-page-section-text, .yt-new-promo-page-header
	{
		color: var(--dimer-text);
	}
	.yt-new-promo-page-button
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: .9;
	}
	.yt-new-promo-page-button:hover
	{
		opacity: 1;
	}
	/* Crude logo fix */
	.yt-new-promo-page-logo
	{
		filter: grayscale(100%) invert(100%);
	}
	/* Subscription button hover fix */
	.yt-uix-subscription-button:hover + .yt-uix-subscription-preferences-button{
		border-left-color: var(--main-color) !important;
	}
	/* Account settings colors */
	.desktop-notifications .browser{
		border: 1px solid var(--dimer-text);
		background: transparent;
	}
	.account-content, #creator-sidebar.single-section .creator-sidebar-section .creator-sidebar-item-link, h2.account-section-header{
		color: var(--dimer-text);
	}


	/*Text and link color for S?-E? */
	.standalone-collection-badge-renderer-text, .standalone-collection-badge-renderer-text a {
		color: var(--dimer-text);
	}
	.standalone-collection-badge-renderer-text {
		color: var(--dimer-text);
		opacity: .8;
	}

  /*
   * Fixes for Creator Studio, preferences and uploading UI
   */

	/* Upload UI */

  .upload-time-remaining, .tabs a, .item-title.upload-cursor-pointer, #creator-editor-title-link{
    color: var(--main-text) !important;
  }
  .progress-bar-progress{
    filter: grayscale(100%) invert(100%) brightness(300%);
    border: none !important;
  }
  .progress-bar-uploading{
    border: 1px solid var(--main-background) !important;
  }
  .progress-bar-text, .progress-bar-text-done, .progress-bar-text-upload-queued{
    color: var(--dimer-text) !important;
  }
  .progress-bar-background{
    border: none;
  }
  .yt-uix-form-input-textarea-container textarea, .yt-uix-form-input-placeholder-container input, #insight input, #insight button{
    color: var(--main-text);
    background-color: var(--main-background);
  }

  /* Analytics */

  /* text color */
  .yt-uix-sessionlink, .comment-comment, .vm-video-side-view-count a, .advanced-text-input.clearfix, #vm-view-filter-label, a.tab, .tab a, .creator-subheader-help-text, .yt-user-name, .account-status-copyright-status-v2-details, .account-status-community-guidelines-status-v2-details, span[class^="account-features"] a, .creator-onboarding-column, #creator-onboarding-container h1, .C2KYWWB-N-g h2 a, .yt-chip{
    color: var(--main-text) !important;
  }
  /* text shadow */
  [class^="dashboard-stat"], .add-widget-button-label, .yt-uix-form-input-select{
    text-shadow: 0 1px 0 var(--hover-background) !important;
  }

  /* comment box - probably just add to existing comment box rules */
  .comment-footer-action.yt-commentbox-show-reply{
    color: var(--main-text) !important;
  }
  .mod-button-content.yt-uix-tooltip button, #creator-page-content .yt-uix-creator-heart-button, .sprite_delete, .sprite_flag{
    filter: invert(100%);
  }
  [contenteditable="true"], .C2KYWWB-h-i, .video-settings-add-tag{
    border-color: var(--hover-background);
    color: var(--main-text);
    background-color: var(--main-background);
  }

  /* icons */

  /* restore original image */
  .add-widget-button{
    background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-videomanager-vfl84Xb-i.png) 0 -359px !important;
  }
  .vm-video-title-badges .yt-uix-tooltip{
    filter: invert(100%);
  }

  /* input */

  /* fix for messed up stacking order due to filter */
  .yt-uix-form-input-checkbox-element{
    pointer-events: none;
  }
  #vm-myvideos-search-box, #vm-myvideos-search-btn{
    border: 1px solid var(--second-background) !important;
  }

  /* background */
  .C2KYWWB-N-m span, #creator-sidebar li.creator-sidebar-item.selected > a{
    background: var(--main-color) !important;
    color: var(--main-text) !important;
  }

  /* buttons */
  .vm-improve-video-button-group:hover .vm-improve-video-button, .vm-improve-video-button-group:hover .vm-improve-video-close-button, .vm-video-suggestion:hover .vm-video-suggestion-button, .vm-video-suggestion:hover .vm-video-suggestion-close-button {
    border: 1px solid var(--main-color) !important;
  }
  .vm-improve-video-button img, .live-welcome-webcam-icon, .live-welcome-engage-icon, .live-welcome-controls-icon, .C2KYWWB-g-a.C2KYWWB-g-f.C2KYWWB-g-h.C2KYWWB-h-b img{
    /* may need some adjusting because of lighter color sets */
    filter: invert(100%);
  }
  .vm-improve-video-button:hover, .vm-improve-video-close-button:hover, .vm-video-suggestion-button:hover, .vm-video-suggestion-close-button:hover {
    color: var(--main-text);
    background-color: var(--second-background);
  }

  /* colors */
  .status-scale-great{
    filter: invert(100%) hue-rotate(180deg) saturate(300%);
  }

  .C2KYWWB-N-o, .C2KYWWB-N-n, .C2KYWWB-a-a, .C2KYWWB-b-d, .C2KYWWB-ib-p, .C2KYWWB-f-a.C2KYWWB-f-b, .gwt-HTML.C2KYWWB-f-f, .C2KYWWB-P-c, .C2KYWWB-P-C, .C2KYWWB-P-x, .C2KYWWB-P-v .C2KYWWB-P-w, .C2KYWWB-P-s .C2KYWWB-P-y, .C2KYWWB-P-s .C2KYWWB-P-B, .popupContent ul, .popupContent li, .C2KYWWB-lb-d, .C2KYWWB-R-l tr, .C2KYWWB-ab-F.C2KYWWB-R-n, .C2KYWWB-Q-o, .C2KYWWB-Q-n, .C2KYWWB-Q-p, .C2KYWWB-Q-j, .C2KYWWB-ab-E.C2KYWWB-ab-z, .C2KYWWB-ab-F, .C2KYWWB-ab-G, .C2KYWWB-ab-H, .C2KYWWB-ab-v, .C2KYWWB-vb-c, .C2KYWWB-vb-a.C2KYWWB-vb-h.C2KYWWB-vb-b, .C2KYWWB-vb-g.C2KYWWB-vb-h td, .C2KYWWB-fb-b, .gwt-viz-container, .C2KYWWB-F-j.C2KYWWB-M-b, .C2KYWWB-O-c, .C2KYWWB-F-d, .C2KYWWB-cb-a, .C2KYWWB-cb-a li, .C2KYWWB-s-f, .C2KYWWB-s-p, .C2KYWWB-f-a, .C2KYWWB-p-h, .C2KYWWB-A-a, .C2KYWWB-p-b, .C2KYWWB-p-q, .C2KYWWB-p-a.C2KYWWB-p-c, .C2KYWWB-p-a.C2KYWWB-p-r{
      background-color: var(--main-background) !important;
      color: var(--main-text) !important;
  }
  .C2KYWWB-F-d p, #gwt-debug-missingDataPanel > div p{
    color: var(--main-background) !important;
  }
  .C2KYWWB-F-d{
    background: transparent !important;
  }
  .C2KYWWB-Q-i{
    background: var(--main-background) !important;
  }

  .C2KYWWB-Q-j:hover {
    background-color: var(--second-background);
    background-image: linear-gradient(-180deg,var(--second-background) 0%,var(--hover-background) 100%);
  }

  .C2KYWWB-g-f, .C2KYWWB-g-v:hover, .C2KYWWB-g-f:hover, .C2KYWWB-g-w:hover {
    border-color: var(--hover-background);
    background-image: linear-gradient(top, var(--second-background) 0, var(--hover-background) 100%);
    background-image: -webkit-linear-gradient(top,var(--second-background) 0,var(--hover-background) 100%);
    background-image: -moz-linear-gradient(top,var(--second-background) 0,var(--hover-background) 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,var(--second-background)),color-stop(100%,var(--hover-background)));
  }
  .C2KYWWB-g-f:focus, .C2KYWWB-g-v:focus, .C2KYWWB-g-w:focus {
    border-color: var(--main-color);
    -moz-box-shadow: 0 0 5px rgba(63,118,183,0.5),inset 0 0 10px var(--main-background) !important;
    -webkit-box-shadow: 0 0 5px rgba(63,118,183,0.5),inset 0 0 10px var(--main-background) !important;
    box-shadow: 0 0 5px rgba(63,118,183,0.5),inset 0 0 10px var(--main-background) !important;
  }
  .C2KYWWB-g-f {
    text-shadow: 0 1px 0 var(--second-background);
    background-color: var(--second-background);
  }
  input.C2KYWWB-l-f {
    color: var(--main-text);
    border: 1px solid var(--hover-background);
    border-top-color: var(--hover-background);
  }
  .C2KYWWB-H-h img, #gwt-debug-settings-button img, .C2KYWWB-ib-b, .C2KYWWB-u-a.C2KYWWB-Q-m, .C2KYWWB-u-a, .C2KYWWB-F-g img, .C2KYWWB-cb-a li img{
    filter: invert(100%);
  }
  .C2KYWWB-X-e.C2KYWWB-a-a, .C2KYWWB-t-a, .C2KYWWB-P-C .C2KYWWB-P-x, .C2KYWWB-P-m, .C2KYWWB-P-x.C2KYWWB-P-v.C2KYWWB-P-p, .C2KYWWB-P-z, .C2KYWWB-P-x, .C2KYWWB-P-c, .C2KYWWB-ib-d tr, .C2KYWWB-ib-m tr, .C2KYWWB-ib-y, .C2KYWWB-Q-i, .C2KYWWB-Q-j, .C2KYWWB-W-e, .C2KYWWB-W-a, .C2KYWWB-ab-v, .C2KYWWB-ab-E, .C2KYWWB-cb-a, .C2KYWWB-cb-a li, .C2KYWWB-f-a, .C2KYWWB-p-h.C2KYWWB-p-f, .C2KYWWB-p-a.C2KYWWB-p-c, .C2KYWWB-p-a.C2KYWWB-p-c.C2KYWWB-p-n, .C2KYWWB-p-q, .C2KYWWB-p-b, .C2KYWWB-p-h.C2KYWWB-p-u, .C2KYWWB-p-h.C2KYWWB-p-p.C2KYWWB-p-u{
    border-color: var(--hover-background) !important;
  }
  .C2KYWWB-cb-a li:hover {
    background-image: linear-gradient(top,var(--second-background) 0,var(--second-background) 100%);
    background-image: -webkit-linear-gradient(top,var(--second-background) 0,var(--second-background) 100%);
    background-image: -moz-linear-gradient(top,var(--second-background) 0,var(--second-background) 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,var(--second-background)),color-stop(100%,var(--second-background)));
  }
  .C2KYWWB-ib-d tr, .C2KYWWB-ib-m tr, .C2KYWWB-ib-y, .C2KYWWB-ib-e tr{
    background-color: var(--main-background) !important;
  }
  .C2KYWWB-g-v:active, .C2KYWWB-g-f:active, .C2KYWWB-g-w:active, .C2KYWWB-g-f.C2KYWWB-g-y {
    border-color: var(--hover-background);
    background-color: var(--second-background);
  }

  /* graphs */
  svg rect[fill="#ffffff"]{
    fill: var(--main-background) !important;
  }
  svg text[fill="#333333"]{
    fill: var(--main-text) !important;
  }
  svg rect[fill="#cccccc"]{
    fill: var(--second-background);
  }
  svg text[fill="#444444"]{
    fill: var(--dimer-text);
    stroke: none;
  }
  svg g text{
    fill: var(--main-text) !important;
    stroke: none !important;
  }

  /* .google-visualization-tooltip */
  svg path[fill="#ffffff"][stroke="#cccccc"] ~ g text[fill="#333333"]{
    fill: var(--main-background) !important;
  }
  svg path[fill="#ffffff"][stroke="#cccccc"] ~ g text[fill="#000000"]{
    fill: var(--main-background) !important;
  }

sourceCode:
/* ==UserStyle==
@name YouTube DeepDark Material
@namespace github.com/RaitaroH/YouTube-DeepDark
@homepageURL https://github.com/RaitaroH/YouTube-DeepDark
@version 2.5.9
@updateURL https://rawgit.com/RaitaroH/YouTube-DeepDark/master/YouTubeDeepDarkMaterial.user.css
@description YouTube dark theme for the Material version of YouTube (2017)
@author RaitaroH
@license GNU-V3.0

@preprocessor uso
@var select mainColor 'Main color' {
	"DeepDark": "#00adee",
	"BreezeDark": "#3DAEE9",
	"Vertex Dark": "#4080fb",
	"Arc Dark": "#5294E2",
	"Firefox Dark": "#5675B9",
	"Firefox57": "#4080FB",
	"Discord": "#7289DA",
	"YouTubeDark": "#E52117",
	"Mint-Y-Dark": "#9AB87C",
	"9anime": "#723f8c",
	"Black&White": "#ffffff",
	"Yellow_petrocompletions": "#FFC700",
	"Yellow2": "#FFC700",
	"Ubuntu_grey": "#EF7847",
	"Ubuntu_purple": "#EF7847",
	"Orange": "#ff6905",
	"Jisho_夜明け":  "#EF7D6C"
}

@var select mainBackground 'Main background color' {
	"DeepDark": "#111111",
	"BreezeDark": "#232629",
	"Vertex Dark": "#2B2B2C",
	"Arc Dark": "#343944",
	"Firefox Dark": "#272B35",
	"Firefox57": "#0C0C0D",
	"Discord": "#1E2124",
	"YouTubeDark": "#111111",
	"Mint-Y-Dark": "#2F2F2F",
	"9anime": "#0B0A0D",
	"Black&White": "#000000",
	"Yellow_petrocompletions": "#141414",
	"Yellow2": "#0a0800",
	"Ubuntu_grey": "#312D2A",
	"Ubuntu_purple": "#2C071A",
	"Orange": "#0a0400",
	"Jisho_夜明け":  "#332222"
}

@var select secondBackground 'Second background color' {
	"DeepDark": "#181818",
	"BreezeDark": "#2a2e32",
	"Vertex Dark": "#353638",
	"Arc Dark": "#383C4A",
	"Firefox Dark": "#181D20",
	"Firefox57": "#252526",
	"Discord": "#2F3136",
	"YouTubeDark": "#232323",
	"Mint-Y-Dark": "#383838",
	"9anime": "#17151C",
	"Black&White": "#0a0a0a",
	"Yellow_petrocompletions": "#202222",
	"Yellow2": "#0c0a04",
	"Ubuntu_grey": "#3D3C38",
	"Ubuntu_purple": "#430B28",
	"Orange": "#0e0702",
	"Jisho_夜明け":  "#2A1B1B"
}

@var select hoverBackground 'Hover background color' {
	"DeepDark": "#232323",
	"BreezeDark": "#31363b",
	"Vertex Dark": "#515254",
	"Arc Dark": "#414A59",
	"Firefox Dark": "#353A44",
	"Firefox57": "#323234",
	"Discord": "#484B51",
	"YouTubeDark": "#343434",
	"Mint-Y-Dark": "#404040",
	"9anime": "#1E1C25",
	"Black&White": "#0f0f0f",
	"Yellow_petrocompletions": "#353838",
	"Yellow2": "#0f0d05",
	"Ubuntu_grey": "#59564D",
	"Ubuntu_purple": "#520D30",
	"Orange": "#110903",
	"Jisho_夜明け":  "#863B2F"
}

@var select mainText 'Main text color' {
	"DeepDark": "#eff0f1",
	"BreezeDark": "#eff0f1",
	"Vertex Dark": "#F3F3F5",
	"Arc Dark": "#C1C8D1",
	"Firefox Dark": "#E3EEF9",
	"Firefox57": "#F9F9FA",
	"Discord": "#FFFFFF",
	"YouTubeDark": "#E1E1E1",
	"Mint-Y-Dark": "#FFFFFF",
	"9anime": "#B4B4B4",
	"Black&White": "#b4b4b4",
	"Yellow_petrocompletions": "#EFF0F1",
	"Yellow2": "#fffdf5",
	"Ubuntu_grey": "#F2F1EF",
	"Ubuntu_purple": "#F2F1EF",
	"Orange": "#fff9f5",
	"Jisho_夜明け":  "#EFB26C"
}

@var select dimerText 'Secondary text color' {
	"DeepDark": "#CCCCCC",
	"BreezeDark": "#bdc3c7",
	"Vertex Dark": "#AEAFB0",
	"Arc Dark": "#B3BAC5",
	"Firefox Dark": "#606376",
	"Firefox57": "#D0D0D0",
	"Discord": "#ADA8AA",
	"YouTubeDark": "#7F7F7F",
	"Mint-Y-Dark": "#D5DADA",
	"9anime": "#747474",
	"Black&White": "#828282",
	"Yellow_petrocompletions": "#9F9999",
	"Yellow2": "#fff8e1",
	"Ubuntu_grey": "#E6E5E3",
	"Ubuntu_purple": "#E6E5E3",
	"Orange": "#ffede1",
	"Jisho_夜明け":  "#986E3F"
}

==/UserStyle== */

@-moz-document regexp('https?:\/\/(?!gaming\.).*youtube\.com\/.*') {

/*For the new Material Design for YouTube*/
/*Theme made by RaitaroH @https://github.com/RaitaroH/YouTube-DeepDark*/

/*GNU General Public License v3.0.*/

/*2.5.9*/

	/*Main color variables*/
	:root
	{
		/*User colors*/
		--main-color: /*[[mainColor]]*/;
		--main-background: /*[[mainBackground]]*/;
		--second-background: /*[[secondBackground]]*/;
		--hover-background: /*[[hoverBackground]]*/;
		--main-text: /*[[mainText]]*/;
		--dimer-text: /*[[dimerText]]*/;

		/*DeepDark colors*/
		/*
		--main-color: #00adee;
		--main-background: #111111;
		--second-background: #181818;
		--hover-background: #232323;
		--main-text: #eff0f1;
		--dimer-text: #CCCCCC;
		*/

		/*KDE colors*/
		/*
		--main-color: #3DAEE9;
		--main-background: #232629;
		--second-background: #2a2e32;
		--hover-background: #31363b;
		--main-text: #eff0f1;
		--dimer-text: #bdc3c7;
		*/

		/*Vertex Dark colors*/
		/*
		--main-color: #4080fb;
		--main-background: #2B2B2C;
		--second-background: #353638;
		--hover-background: #515254;
		--main-text: #F3F3F5;
		--dimer-text: #AEAFB0;
		*/

		/*Arc Dark colors*/
		/*
		--main-color: #5294E2;
		--main-background: #343944;
		--second-background: #383C4A;
		--hover-background: #414A59;
		--main-text: #C1C8D1;
		--dimer-text: #B3BAC5;
		*/

		/*Firefox Dark colors*/
		/*
		--main-color: #5675B9;
		--main-background: #272B35;
		--second-background: #181D20;
		--hover-background: #353A44;
		--main-text: #E3EEF9;
		--dimer-text: #606376;
		*/

		/*Firefox57 Dark colors*/
		/*
		--main-color: #4080FB;
		--main-background: #0C0C0D;
		--second-background: #252526;
		--hover-background: #323234;
		--main-text: #F9F9FA;
		--dimer-text: #D0D0D0;
		*/

		/*Discord colors*/
		/*
		--main-color: #7289DA;
		--main-background: #1E2124;
		--second-background: #2F3136;
		--hover-background: #484B51;
		--main-text: #FFFFFF;
		--dimer-text: #ADA8AA;
		*/

		/*YouTube dark colors*/
		/*
		--main-color: #E52117;
		--main-background: #111111;
		--second-background: #232323;
		--hover-background: #343434;
		--main-text: #E1E1E1;
		--dimer-text: #7F7F7F;
		*/

		/*YouTube light colors*/
		/*
		--main-color: #E52117;
		--main-background: #F9F9F9;
		--second-background: #FFFFFF;
		--hover-background: #EAEAEA;
		--main-text: #111111;
		--dimer-text: #43434B;
		*/

		/*Mint-Y-Dark colors*/
		/*
		--main-color: #9AB87C;
		--main-background: #2F2F2F;
		--second-background: #383838;
		--hover-background: #404040;
		--main-text: #FFFFFF;
		--dimer-text: #D5DADA;
		*/

		/*9anime colors*/
		/*
		--main-color: #723f8c;
		--main-background: #0B0A0D;
		--second-background: #17151C;
		--hover-background: #1E1C25;
		--main-text: #B4B4B4;
		--dimer-text: #747474;
		*/

		/*Black and white*/
		/*
		--main-color: #ffffff;
		--main-background: #000000;
		--second-background: #0a0a0a;
		--hover-background: #0f0f0f;
		--main-text: #b4b4b4;
		--dimer-text: #828282;
		*/

		/*Yellow (colors from petrocompletions)*/
		/*
		--main-color: #FFC700;
		--main-background: #141414;
		--second-background: #202222;
		--hover-background: #353838;
		--main-text: #EFF0F1;
		--dimer-text: #9F9999;
		*/

		/*Yellow 2*/
		/*
		--main-color: #ffc700;
		--main-background: #0a0800;
		--second-background: #0c0a04;
		--hover-background: #0f0d05;
		--main-text: #fffdf5;
		--dimer-text: #fff8e1;
		*/

		/*Ubuntu grey colors*/
		/*
		--main-color: #EF7847;
		--main-background: #312D2A;
		--second-background: #3D3C38;
		--hover-background: #59564D;
		--main-text: #F2F1EF;
		--dimer-text: #E6E5E3;
		*/

		/*Ubuntu purple colors*/
		/*
		--main-color: #EF7847;
		--main-background: #2C071A;
		--second-background: #430B28;
		--hover-background: #520D30;
		--main-text: #F2F1EF;
		--dimer-text: #E6E5E3;
		*/

		/*Orange*/
		/*
		--main-color: #ff6905;
		--main-background: #0a0400;
		--second-background: #0e0702;
		--hover-background: #110903;
		--main-text: #fff9f5;
		--dimer-text: #ffede1;
		*/

