	}
	.yt-live-chat-text-message-renderer-1, .yt-live-chat-text-message-renderer-0, .yt-live-chat-item-list-renderer-1 #item-scroller.yt-live-chat-item-list-renderer, .yt-live-chat-item-list-renderer-0 #item-scroller.yt-live-chat-item-list-renderer
	{
		background-color: var(--main-background) !important;
	}
	.yt-live-chat-text-message-renderer-1:hover, .yt-live-chat-text-message-renderer-0:hover, .ytd-menu-service-item-renderer-0:hover, .ytd-menu-navigation-item-renderer-0 a.ytd-menu-navigation-item-renderer:hover, .ytd-menu-navigation-item-renderer-1 a.ytd-menu-navigation-item-renderer:hover, .yt-emoji-picker-renderer-0 #search-panel.yt-emoji-picker-renderer, .yt-emoji-picker-renderer-1 #search-panel.yt-emoji-picker-renderer, .yt-emoji-picker-category-renderer-0 yt-formatted-string.yt-emoji-picker-category-renderer, .yt-emoji-picker-category-renderer-1 yt-formatted-string.yt-emoji-picker-category-renderer, #unfocused.yt-live-chat-text-input-field-renderer, yt-live-chat-message-renderer
	{
		background-color: var(--hover-background) !important;
	}
	.yt-live-chat-header-renderer-1, .yt-live-chat-renderer-1, .yt-live-chat-message-input-renderer-1 #author-name.yt-live-chat-message-input-renderer, .yt-live-chat-text-input-field-renderer-1 #input.yt-live-chat-text-input-field-renderer, .yt-live-chat-header-renderer-0, .yt-live-chat-renderer-0, .yt-live-chat-message-input-renderer-0 #author-name.yt-live-chat-message-input-renderer, .yt-live-chat-text-input-field-renderer-0 #input.yt-live-chat-text-input-field-renderer, .yt-live-chat-text-message-renderer-0 #message.yt-live-chat-text-message-renderer, .yt-live-chat-text-message-renderer-1 #message.yt-live-chat-text-message-renderer, .yt-live-chat-message-input-renderer-0 #count.yt-live-chat-message-input-renderer, .yt-live-chat-message-input-renderer-1 #count.yt-live-chat-message-input-renderer, .yt-live-chat-renderer-1, .yt-live-chat-renderer-0, .ytd-menu-navigation-item-renderer-0 yt-formatted-string.ytd-menu-navigation-item-renderer, .ytd-menu-navigation-item-renderer-1 yt-formatted-string.ytd-menu-navigation-item-renderer, .ytd-menu-service-item-renderer-0 yt-formatted-string.ytd-menu-service-item-renderer, .ytd-menu-service-item-renderer-1 yt-formatted-string.ytd-menu-service-item-renderer, .paper-menu-0, .paper-menu-1, .yt-emoji-picker-renderer-0 #search-panel.yt-emoji-picker-renderer, .yt-emoji-picker-renderer-1 #search-panel.yt-emoji-picker-renderer, .paper-input-container-0 .input-content.paper-input-container input, .paper-input-container-0 .input-content.paper-input-container textarea, .paper-input-container-0 .input-content.paper-input-container iron-autogrow-textarea, .paper-input-container-0 .input-content.paper-input-container .paper-input-input, .paper-input-container-1 .input-content.paper-input-container input, .paper-input-container-1 .input-content.paper-input-container textarea, .paper-input-container-1 .input-content.paper-input-container iron-autogrow-textarea, .paper-input-container-1 .input-content.paper-input-container .paper-input-input, .yt-emoji-picker-category-renderer-0 yt-formatted-string.yt-emoji-picker-category-renderer, .yt-emoji-picker-category-renderer-1 yt-formatted-string.yt-emoji-picker-category-renderer, #message.yt-live-chat-text-message-renderer, #subtext.yt-live-chat-message-renderer
	{
		color: var(--main-text) !important;
	}
	.yt-live-chat-text-input-field-renderer-0 #label.yt-live-chat-text-input-field-renderer, .yt-live-chat-text-input-field-renderer-1 #label.yt-live-chat-text-input-field-renderer, .yt-live-chat-text-message-renderer-0 #timestamp.yt-live-chat-text-message-renderer, .yt-live-chat-text-message-renderer-1 #timestamp.yt-live-chat-text-message-renderer, .yt-live-chat-text-message-renderer #timestamp.yt-live-chat-text-message-renderer, .yt-live-chat-message-input-field-renderer #label.yt-live-chat-text-input-field-renderer, #deleted-state.yt-live-chat-text-message-renderer, #show-original.yt-live-chat-text-message-renderer, yt-live-chat-text-message-renderer[is-deleted] #message.yt-live-chat-text-message-renderer
	{
		color: var(--dimer-text) !important;
	}
	.iron-icon-0, .ytd-button-renderer-0 #button.ytd-button-renderer, .iron-icon
	{
		fill: var(--main-text) !important;
		color: var(--main-text) !important;
	}
	#menu.yt-live-chat-text-message-renderer:hover .style-scope.iron-icon, #overflow.yt-live-chat-header-renderer:hover .style-scope.iron-icon, paper-icon-button:hover .style-scope.iron-icon
	{
		fill: var(--main-color) !important;
	}
	.yt-live-chat-text-message-renderer-0 #author-name.yt-live-chat-text-message-renderer, .yt-live-chat-text-message-renderer-1 #author-name.yt-live-chat-text-message-renderer, #author-name.yt-live-chat-text-message-renderer
	{
		color: var(--main-color) !important;
	}
	.yt-live-chat-text-message-renderer-0 #menu.yt-live-chat-text-message-renderer, .yt-live-chat-text-message-renderer-1 #menu.yt-live-chat-text-message-renderer, #menu.yt-live-chat-text-message-renderer, yt-button-renderer #button.yt-button-renderer
	{
		background: transparent !important;
	}
	/*Sign in chat*/
	.yt-live-chat-message-renderer-0, .yt-live-chat-message-renderer-1, yt-button-renderer.style-dark[is-paper-button]
	{
		color: var(--main-text) !important;
		background-color: var(--second-background) !important;
	}
	.yt-live-chat-message-renderer-0 #subtext.yt-live-chat-message-renderer, .yt-live-chat-message-renderer-1 #subtext.yt-live-chat-message-renderer, #label.yt-live-chat-text-input-field-renderer, .input-content.paper-input-container input, .input-content.paper-input-container textarea, .input-content.paper-input-container iron-autogrow-textarea, .input-content.paper-input-container .paper-input-input
	{
		color: var(--dimer-text) !important;
	}
	.ytd-button-renderer-1.style-dark[is-paper-button]:hover, .ytd-button-renderer-0.style-dark[is-paper-button]:hover, #search-panel.yt-emoji-picker-renderer
	{
		background-color: var(--hover-background) !important;
	}
	.ytd-button-renderer-1.style-dark[is-paper-button], .ytd-button-renderer-0.style-dark[is-paper-button], paper-icon-button.yt-live-chat-item-list-renderer, #focused.yt-live-chat-text-input-field-renderer
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	/*Go down button in chat*/
	paper-icon-button.yt-live-chat-item-list-renderer
	{
		opacity: .9;
	}
	paper-icon-button.yt-live-chat-item-list-renderer:hover, paper-icon-button.yt-live-chat-item-list-renderer:hover .style-scope.iron-icon
	{
		opacity: 1 !important;
		fill: var(--main-text) !important;
	}

	/*Resizing the top bar and it's spacing to get some extra vertical space*/
	/*
	ytd-page-manager
	{
		margin-top: 30px !important;
	}
	#container.ytd-masthead
	{
		height: 40px !important;
	}
	*/

	/*Notification stuff*/
	#notification-count.ytd-notification-topbar-button-renderer
	{
		background-color: var(--main-color) !important;
		box-shadow: -1px 1px var(--main-background);
	}
 	#button:hover
  	{
  		color: var(--main-color) !important;
  	}
	ytd-notification-renderer.unseen #new.ytd-notification-renderer
	{
		background-color: var(--main-color) !important;
	}
	.metadata.ytd-notification-renderer
	{
		color: var(--dimer-text) !important;
		opacity: .8;
	}
	.metadata.ytd-notification-renderer:hover
	{
		color: var(--main-color) !important;
		opacity: 1 !important;
	}

	/*Confirmantion popup*/
	paper-toast
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}

	/*Cards*/
	.iv-card-content,.ytp-ce-expanding-overlay-background,.ytp-cards-teaser .ytp-cards-teaser-text, .ytp-cards-teaser .ytp-cards-teaser-box
	{
		background-color: var(--main-background) !important;
	}
	.ytp-cards-teaser .ytp-cards-teaser-box,.iv-card-image
	{
		border: 0px !important;
	}
	.iv-card-content > :first-child,.html5-video-player a,.iv-card h2,.ytp-cards-teaser .ytp-cards-teaser-text
	{
		color: var(--main-text) !important;
	}
	.iv-card:hover .iv-card-primary-link,.ytp-ce-website-title
	{
		color: var(--main-color) !important;
	}
	.ytp-ce-element:hover
	{
		border-color: var(--main-color) !important;
	}
	.ytp-ce-channel-title.ytp-ce-link
	{
		color: var(--main-color) !important;
		opacity: 0.9 !important;
	}
	.ytp-ce-channel-title.ytp-ce-link:hover,.ytp-cards-teaser .ytp-cards-teaser-text:hover
	{
		color: var(--main-color) !important;
		opacity: 1 !important;
	}
	.ytp-ce-channel-metadata.yt-ui-ellipsis.yt-ui-ellipsis-3
	{
		background: transparent;
		color: var(--dimer-text) !important;
	}
	/*Cards border color*/
	.iv-card-message
	{
		border-bottom: 1px solid var(--main-color) !important;
	}
	.ytp-ce-channel-this .ytp-ce-channel-metadata
	{
		border-color: var(--hover-background) !important;
	}

	/*Poll*/
	.iv-card-poll label, .iv-card-poll.iv-card-poll-voted label.iv-card-poll-choice-checked, .iv-card-poll.iv-card-poll-voted label
	{
		color: var(--main-text) !important;
	}
	.iv-card-poll .iv-card-poll-result .iv-card-poll-result-bar
	{
		background-color: var(--main-color) !important;
	}

	/*Account menu header*/
	ytd-active-account-header-renderer:hover
	{
		background-color: var(--hover-background) !important;
	}

	paper-item.ytd-account-settings
	{
		color: var(--dimer-text) !important;
		opacity: .8;
	}
	paper-item.ytd-account-settings:hover
	{
		opacity: 1 !important;
	}

	/*Wathced videos dark background*/
	ytd-thumbnail-overlay-playback-status-renderer
	{
		background-color: hsla(0, 0%, 6.7%, .4) !important;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0;
		align-items: left;
		justify-content: left;
	}
	ytd-thumbnail-overlay-playback-status-renderer:hover
	{
		background-color: hsla(0, 0%, 6.7%, .2) !important;
	}

	/*Live background color*/
	ytd-thumbnail-overlay-time-status-renderer[overlay-style="LIVE"]
	{
    		background-color: var(--main-color) !important;
	}


	/*Recolored sidepane icons, menu icons and filter button*/
	.guide-icon.ytd-guide-entry-renderer, paper-button.ytd-toggle-button-renderer, yt-icon.ytd-compact-link-renderer, yt-icon.ytd-toggle-theme-compact-link-renderer
	{
		color: var(--dimer-text) !important;
	}

	/*Sidepane titles*/
	#guide-section-title a
	{
		color: var(--dimer-text) !important;
	}
	#guide-section-title a:hover
	{
		color: var(--main-color) !important;
	}


	/*Autoplay text*/
	#autoplay.ytd-compact-autoplay-renderer
	{
		color: var(--dimer-text) !important;
		opacity: .8;
	}

	/*Links in video description*/
	.description.ytd-video-secondary-info-renderer a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}

	/*Author name*/
	#owner-name.ytd-video-owner-renderer
	{
		opacity: .9;
	}
	#owner-name.ytd-video-owner-renderer:hover
	{
		opacity: 1 !important;
	}

	/*Set a memento*/
	ytd-toggle-button-renderer.style-compact-gray[is-paper-button]
	{
		background-color: var(--second-background) !important;
		color: var(--dimer-text) !important;
	}

	/*Series name*/
	.super-title.ytd-video-primary-info-renderer a, #additional-metadata-line.ytd-video-meta-block
	{
		color: var(--dimer-text) !important;
		opacity: .8;
	}

	#selectionBar.paper-tabs
	{
    		border-color: var(--main-color) !important;
	}

	/*YouTube Apps*/
	yt-multi-page-menu-section-renderer.style-scope div ytd-compact-link-renderer a paper-item div yt-icon svg g path[fill="#F00"],
	yt-multi-page-menu-section-renderer.style-scope div ytd-compact-link-renderer a paper-item div yt-icon svg g path[fill="#f00"],
	yt-multi-page-menu-section-renderer.style-scope div ytd-compact-link-renderer a paper-item div yt-icon svg g path[fill="#FF8983"],
	yt-multi-page-menu-section-renderer.style-scope div ytd-compact-link-renderer a paper-item div yt-icon svg g path[fill="#E23229"],
	yt-multi-page-menu-section-renderer.style-scope div ytd-compact-link-renderer a paper-item div yt-icon svg g path[fill="#C7322A"]
	{
		fill: var(--main-color) !important;
	}

	/*"Best" of YouTube icons in sidepane*/
	.ytd-guide-section-renderer img.yt-img-shadow
	{
		filter: grayscale(100%);
	}

	/* Promo page */
	body.yt-new-promo-page-use-launched-copy .yt-new-promo-page-header
	{
		background: var(--main-background) !important;
	}
	.yt-new-promo-page-section-text, .yt-new-promo-page-header
	{
		color: var(--dimer-text);
	}
	.yt-new-promo-page-button
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: .9;
	}
	.yt-new-promo-page-button:hover
	{
		opacity: 1;
	}
	/* Crude logo fix */
	.yt-new-promo-page-logo
	{
		filter: grayscale(100%) invert(100%);
	}
	/* Fix for active like/dislike buttons */
	ytd-toggle-button-renderer.style-grey-text[is-icon-button] {
		color: var(--yt-button-color);
	}
	ytd-toggle-button-renderer #button.ytd-toggle-button-renderer {
		color: inherit;
	}
	/*like/dislike buttons in nested replies*/
	.style-scope.ytd-toggle-button-renderer svg
	{
		fill: var(--dimer-text) !important;
	}
	.style-scope.ytd-toggle-button-renderer yt-icon-button.style-default-active svg
	{
		fill: var(--main-color) !important;
	}

