@-moz-document domain("userstyles.org") {

/*Theme made by RaitaroH @https://github.com/RaitaroH/Userstyles-DeepDark*/

/*GNU General Public License v3.0*/

/*1.1.6*/

	/*Main color variables*/
	:root
