	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
		text-decoration: none !important;
	}

	/*Body*/
	body
	{
		background-color: var(--main-background) !important;
	}

	/*Logo*/
	.logo
	{
		filter: grayscale(100%) brightness(300%);
	}

	/*Header*/
	#header
	{
		background-color: var(--hover-background) !important;
	}
	#search_input
	{
		background-color: var(--second-background) !important;
	}
	#input_text_search
	{
		color: var(--main-text) !important;
	}
	.Combined-Shape[src*="blue"]
	{
		filter: brightness(50%);
	}
	.Combined-Shape[src*="blue"]
	{
		filter: grayscale(100%) brightness(200%);
	}
	#create_new_style a
	{
		color: var(--main-color) !important;
	}
	#create_new_style
	{
		color: var(--main-color) !important;
		border-color: var(--main-color) !important;
		opacity: .9;
	}
	#create_new_style:hover
	{
		opacity: 1;
	}
	.header_button a
	{
		color: var(--main-text) !important;
	}
	.header_button a:hover
	{
		color: var(--main-color) !important;
	}

	/*Sidebar*/
	#nav-view, #navigation
	{
		background-color: var(--main-background) !important;
	}
	.linksBox .navigation_link:last-child, .navigation_link
	{
	    box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
	}
	.navigation_category
	{
		color: var(--main-text) !important;
	}


	/*Tabs*/
	.navigation_link:hover
	{
	    background-color: var(--hover-background) !important;
	}
	.navigation_link_active
	{
	    border-color: var(--main-color) !important;
	}
	.navigation_title
	{
		color: var(--main-text) !important;
	}
	.navigation_link:hover .navigation_title
	{
		color: var(--main-color) !important;
	}
	.logo_navigation_link[src*="svg"]
	{
		filter: invert(100%) grayscale(100%);
	}


	/*Slideshow*/
	#left-arrow-carousel, #right-arrow-carousel
	{
		filter: invert(100%);
	}
	#overlay-info
	{
		background-color: var(--second-background) !important;
	}
	#style-info-title-carousel
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	#style-info-author-carousel a
	{
		color: var(--main-text) !important;
	}
	#style-info-author-carousel a:hover, #style-info-title-carousel:hover
	{
		color: var(--main-color) !important;
		opacity: 1;
	}
	#overlay-info
	{
		border-radius: 0 !important;
	}
	.oval
	{
		background-color: var(--dimer-text) !important;
	}
	.oval-active
	{
		background-color: var(--main-color) !important;
		box-shadow: none !important;
	}


	/*Categories*/
	.category_title
	{
		color: var(--main-text) !important;
	}
	.see_more
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: .9;
	}
	.see_more:hover
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: 1 !important;
	}


	/*Cards*/
	.style_card
	{
		background-color: var(--second-background) !important;
	}
	.screenshot_wrapper
	{
		border-color: var(--hover-background) !important;
	}
	h4 a, .style_updated, .k, .style_card h4 a
	{
