	{
		filter: grayscale(100%) brightness(200%);
	}
	.current, .div-arrow
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		border: none !important;
		opacity: .9;
	}
	.pageNumber
	{
		color: var(--main-text) !important;
	}
	.pageNumber:hover
	{
		color: var(--main-color) !important;
	}
	.div-arrow:hover, .current:hover
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: 1;
	}

	.arrow-icon[src="/ui/images/icons/right-arrow-icon.svg"], .arrow-icon[src="/ui/images/icons/left-arrow-icon.svg"]
	{
		filter: grayscale(100%) brightness(200%);
	}
	.style_card_long, .line_seperator
	{
		box-shadow: inset 0 -1px 0 0 var(--hover-background) !important;
	}
	#title_long a, .style_card_long #title_long a
	{
		color: var(--main-text) !important;
	}
	#title_long a:hover, .style_card_long:hover #title_long a, .style_author_long a
	{
		color: var(--main-color) !important;
	}
	.style_description_long, .style_author_long
	{
		color: var(--dimer-text) !important;
	}
	.view_details
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: .9;
	}
	.view_details:hover
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: 1;
	}


	/*Style*/
	#preview_image_div
	{
		background-position: center center !important;
	}
	.author-name a, .text_style_page a, #as_userscript a, #notes_textarea a, #discussion_author a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.author-name a:hover, .text_style_page a:hover, #as_userscript a:hover, #as_userscript a:visited, #as_userscript a:visited:hover, #notes_textarea a:hover, #notes_textarea a:visited, #discussion_author a:visited, #discussion_author a:visited:hover, #discussion_author a:hover
	{
		color: var(--main-color) !important;
		opacity: 1;
	}
	#share_div, #headline, #infomation_value_left, #information_key, .title_setting
	{
		color: var(--main-text) !important;
	}
	#information_key_left, .text_style_page, #discussion_date, .by-author, .style_author.ellipsis
	{
		color: var(--dimer-text) !important;
	}
	#information_value, #applies_link
	{
		color: var(--main-color) !important;
	}
	.white_button:hover, .css_button:hover, .advanced_button:hover, #archive_style_button:hover, #send_feedback_button:hover, #style_installed_button
	{
		background-color: var(--hover-background) !important;
		color: var(--main-color) !important;
		border: none !important;
		box-shadow: none !important;
	}
	.white_button, .css_button, .advanced_button, #archive_style_button, #send_feedback_button, #style_installed_button:hover
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		border: none !important;
		box-shadow: none !important;
	}
	#update_style_button, #install_style_button
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		border: none !important;
		box-shadow: none !important;
		opacity: .9;
	}
	#update_style_button:hover, #install_style_button:hover
	{
		opacity: 1;
	}

	#notes_textarea, #stylish-code, .advancedsettings_shown
	{
		background-color: var(--second-background) !important;
		color: var(--main-text) !important;
		border: 1px solid var(--hover-background) !important;
	}
	div.setting_div > div > div
	{
		background-color: black !important;
	}
	#single_discussion
	{
		box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
	}


	/*iframes*/
	p, ul, i, body, .contact-bold-font
	{
		color: var(--dimer-text) !important;
	}
	a, a:visited, .sub-title
	{
		color: var(--main-color) !important;
		opacity: .9 !important;
	}
	a:hover, a:visited:hover
	{
		color: var(--main-color) !important;
		opacity: 1 !important;
	}
	.contact-font
	{
		color: var(--dimer-text) !important;
	}
	.contact-input
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	:focus
	{
		outline-color: var(--main-color) !important;
	}
	/*login*/
	.btn-auth, input#login-existing, input#password-existing, input[type="submit"]
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimer-text) !important;
	}
	.btn-auth:hover, input[type="submit"]:hover
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	input#remember-normal
	{
		filter: invert(100%);
	}
	a.btn-auth:visited
	{
		color: var(--dimer-text) !important;
	}

	/*Edit style page*/
	input:not([class*="color"]), textarea
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
		color: var(--dimer-text) !important;
	}
	input:active, textarea:active, input:focus, textarea:focus
	{
        	border-color: var(--main-color) !important;
	}
	input[id*="option-default"]
	{
        	border: 1px solid transparent !important;
		box-shadow: var(--shadow) !important
	}
	.edit-setting
	{
		background-color: var(--hover-background) !important;
	}
	/*If your browser shows buttons white because of a default white theme, uncomment this*/
	/*
	input[type="radio"], input#enable-source-editor-code
	{
		filter: invert(100%);
	}
	*/

	/*Edit css*/
	.CodeMirror
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.CodeMirror-linenumber, .CodeMirror-gutters
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.errorExplanation
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}

	/*Code text*/
	.CodeMirror div.CodeMirror-cursor { border-color: var(--main-color); }
	.cm-s-default.CodeMirror { background: var(--second-background); color: var(--main-text); }
	.cm-s-default div.CodeMirror-selected { background: var(--hover-background); }
	.cm-s-default .CodeMirror-line::selection, .cm-s-default .CodeMirror-line > span::selection, .cm-s-default .CodeMirror-line > span > span::selection { background: var(--hover-background); }
	.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-default .CodeMirror-line > span::-moz-selection, .cm-s-default .CodeMirror-line > span > span::-moz-selection { background-color: var(--hover-background); }
	.cm-s-default .CodeMirror-gutters { background: var(--hover-background); border-right: 0px; }
	.cm-s-default .CodeMirror-guttermarker { color: var(--main-text); }
	.cm-s-default .CodeMirror-guttermarker-subtle, .cm-s-default .CodeMirror-linenumber { color: var(--dimer-text); }
	.cm-s-default .CodeMirror-cursor { border-left: 1px solid var(--main-color); }

	.cm-s-default span.cm-comment { color: var(--dimer-text); }
	.cm-s-default span.cm-atom, .cm-s-default span.cm-number { color: var(--main-color); opacity: .6; }

	.cm-s-default span.cm-property, .cm-s-default span.cm-attribute { color: var(--main-color); opacity: 1; }
	.cm-s-default span.cm-keyword, .cm-s-default span.cm-string { color: var(--main-color); filter: hue-rotate(60deg); }
	.cm-s-default span.cm-string.cm-property { color: var(--main-text); }

	.cm-s-default span.cm-variable { color: var(--main-text); }
	.cm-s-default span.cm-variable-2 { color: var(--main-color); opacity: .6; }
	.cm-s-default .cm-variable-3, .cm-s-default .cm-type { color: var(--main-color); filter: hue-rotate(280deg); }
	.cm-s-default span.cm-def { color: var(--main-text); }
	.cm-s-default span.cm-bracket { color: var(--main-text); font-weight: bold; }
	.cm-s-default span.cm-tag { color: var(--main-color); opacity: .6; }
	.cm-s-default span.cm-link, .cm-s-default span.cm-keyword { color: var(--main-color); filter: invert(100%); }
	.cm-s-default span.cm-error { border-bottom: var(--hover-background); color: var(--main-text); }
	.cm-s-default span.cm-qualifier { color: var(--main-text); }

	.cm-s-default .CodeMirror-activeline-background { background: var(--second-background); }
	.cm-s-default .CodeMirror-matchingbracket { color: var(--main-color); }
	.cm-s-default .CodeMirror-matchingtag { background: var(--dimer-text); }

	.cm-s-default .cm-builtin { color: var(--main-color); }
	.cm-s-default .cm-meta { color: var(--dimer-text); }


	/*Forum*/
	#Head
	{
    	background: var(--hover-background) !important;
    	color: var(--main-text) !important;
	}
	#Head a:hover
	{
		color: var(--main-color) !important;
	}


	/*Search*/
	input.DateBox, input.InputBox, input.SmallInput, textarea
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	.ItemDiscussion .Meta, .DataList .Meta
	{
		color: var(--dimer-text) !important;
	}
	mark
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	#search_input input
	{
		border: none !important;
	}
	.SiteSearch .Button
	{
		background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -324px no-repeat transparent !important;
	}
	.SiteSearch .Button:hover
	{
		background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -260px no-repeat transparent !important;
	}


	/*Login*/
	a.TextColor, a .TextColor
	{
		color: var(--main-text) !important;
	}
	a.TextColor:hover, a .TextColor:hover, a:hover .TextColor
	{
		color: var(--main-color) !important;
	}
	.Button
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimer-text) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}
	.Button:hover
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}


	/*Menu*/
	.Sprite16
	{
		filter: invert(100%);
	}
	.Alert
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}


	/*Categories*/
	.PanelCategories li:first-child, .PanelCategories li:nth-last-child(2)
	{
		border-color: var(--hover-background) !important;
	}
	.Count
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	#Panel .FilterMenu li a, .PanelInfo li a
	{
		color: var(--main-text) !important;
	}
	#Panel .FilterMenu li a:hover, .PanelInfo li a:hover
	{
		color: var(--main-color) !important;
	}


	/*Discussions*/
	.Tag
	{
		background: var(--main-background) !important;
		color: var(--main-text) !important;
	}
	.HasNew
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	.MItem, span.ShowDiscussionAuthor, .Pager span
	{
		color: var(--dimer-text) !important;
	}
	.OptionsTitle, a.Bookmark, a.Bookmarked, a.Bookmarking
	{
		filter: invert(80%);
	}
	.OptionsTitle:hover, a.Bookmark:hover, a.Bookmarked:hover, a.Bookmarking:hover
	{
		filter: invert(100%);
	}
	.MenuItems
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	.Flyout:before, .Flyout:after
	{
		border-bottom-color: var(--second-background) !important;
	}
	.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active
	{
		color: var(--main-text) !important;
	}
	.MenuItems a:hover
	{
		background-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	.MenuItems hr
	{
		border-bottom-color: var(--hover-background) !important;
	}
	.Item.Read
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	.Item.Read:hover
	{
		background: var(--hover-background) !important;
	}
	.DataList .ItemDiscussion
	{
		margin-bottom: 10px !important;
		box-shadow: var(--shadow) !important;
	}
	.ItemContent.Discussion
	{
		margin-left: 10px !important;
	}

	/*Replacing images in the forum and users' style page*/
	.DataList .rating-image, .good-average-rating, .ok-average-rating, .bad-average-rating
	{
		display: block !important;
		-moz-box-sizing: border-box !important;
		box-sizing: border-box !important;
		width: 24px !important;
		height: 24px !important;
		padding-left: 24px !important;
		right: -175px !important;
	}
	.style-warning-icon
	{
		-moz-box-sizing: border-box !important;
		box-sizing: border-box !important;
		width: 12px !important;
		height: 12px !important;
		padding-left: 12px !important;
		right: -175px !important;
		margin: 5px auto 5px 5px;
	}
	.DataList .rating-image
	{
		left: 5px;
		top: 6px;
	}
	.good-average-rating, .ok-average-rating, .bad-average-rating
	{
		margin-left: auto;
		margin-right: auto;
	}
	.rating-image.bad-rating, .bad-average-rating
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Userstyles-DeepDark/master/Images/bad.png");
	}
	.rating-image.good-rating, .good-average-rating
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Userstyles-DeepDark/master/Images/good.png");
	}
	.rating-image.ok-rating, .ok-average-rating
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Userstyles-DeepDark/master/Images/ok.png");
	}
	.style-warning-icon
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Userstyles-DeepDark/master/Images/warning.png");
	}
	.author-styles a[href*="edit"], .author-styles a[href*="delete"], .author-styles a[href*="stats"]
	{
		visibility: hidden;
		position: relative;
		width: 24px !important;
		height: 24px !important;
		display: inline-block;
	}
	.author-styles a[href*="edit"]:after, .author-styles a[href*="delete"]:after, .author-styles a[href*="stats"]:after
	{
		visibility: visible;
		position: absolute;
		top: 0;
		left: 0;
		width: 24px !important;
		height: 24px !important;
		display: inline-block;
	}
	.author-styles a[href*="edit"]:after
	{
		content: url("https://raw.githubusercontent.com/RaitaroH/Userstyles-DeepDark/master/Images/edit.png");
	}
	.author-styles a[href*="delete"]:after
	{
		content: url("https://raw.githubusercontent.com/RaitaroH/Userstyles-DeepDark/master/Images/delete.png");
	}
	.author-styles a[href*="stats"]:after
	{
		content: url("https://raw.githubusercontent.com/RaitaroH/Userstyles-DeepDark/master/Images/stats.png");
	}

	/*User styles*/
	.statistic table, th, td
	{
		border: none !important;
		background-color: var(--second-background) !important;
	}
	.numeric-value
	{
		text-align: center;
	}
	td[valign="top"]
	{
		vertical-align: middle;
		padding-left: 10px;
	}
	/*Removed the empty space*/
	.author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(1)
	{
		opacity: 0;
		border: none !important;
	}
	.author-styles tbody tr, .author-styles tbody tr td
	{
		border-bottom: 10px solid var(--main-background) !important;
		box-shadow: var(--shadow) !important;
	}
	.author-styles tbody tr:hover td
	{
		background-color: var(--hover-background) !important;
	}
	.author-styles > tbody:nth-child(2) > tr:nth-child(1) > td:nth-child(7)
	{
		width: 100px;
	}
	.style-warning
	{
		background-color: var(--hover-background) !important;
	}
	td[class="obsolete"] a
	{
		text-decoration: line-through !important;
	}

	.Pager .Highlight, .Pager a:hover
	{
		color: var(--main-color) !important;
	}
	.Pager a
	{
		color: var(--main-text) !important;
	}
	.DataList .ItemDiscussion:hover
	{
		background-color: var(--hover-background);
	}
	.BoxButtons.BoxNewDiscussion a
	{
		color: var(--dimer-text) !important;
	}
	.BoxButtons.BoxNewDiscussion:hover a
	{
		color: var(--main-color) !important;
	}

	/*Preferences*/
	table.PreferenceGroup th, table.PreferenceGroup td, #Form_Invitation th, #Form_Invitation td
	{
		border-color: var(--hover-background) !important;
	}
	table.PreferenceGroup:hover th, table.PreferenceGroup:hover td, #Form_Invitation:hover th, #Form_Invitation:hover td
	{
		background-color: transparent !important;
	}
	.WarningMessage, .Info
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	select
	{
		background-color: var(--hover-background);
		border-color: var(--hover-background);
		color: var(--dimer-text);
	}


	/*Notifications*/
	.Flyout:before, .Flyout:after
	{
		border-bottom-color: var(--hover-background) !important;
	}

	/*Comments*/
	.Attachment:hover .FileHover
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.ReactSprite
	{
		filter: invert(100%);
	}
	blockquote.Quote, blockQuote.UserQuote, code, pre
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	code, pre
	{
		color: var(--main-text) !important;
	}
	.EmoticonBox, .EmoticonContainer, .EmotifyDropdownActive
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	.EmoticonBox:hover
	{
		box-shadow: 1px 1px 1px 1px var(--second-background) !important;
	}
	.FlyoutMenu
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimer-text) !important;
	}
	.InformMessages .InformMessage
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}
	.InformMessages .InformMessage a.Close
	{
		color: var(--main-text) !important;
	}
	.InformMessages .InformMessage a.Close:hover
	{
		color: var(--main-color) !important;
	}

	/*Profile*/
	.NavButton, a.NavButton.ProfileButtons.Button-EditProfile
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimer-text) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}
	.NavButton:hover, a.NavButton.ProfileButtons.Button-EditProfile:hover
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
		box-shadow: none !important;
	}
	.SpEditProfile
	{
		filter: invert(100%);
	}
	dl.About dt
	{
		color: var(--dimer-text) !important;
	}


	/*New Discussion*/
	.DiscussionForm label
	{
		color: var(--dimer-text) !important;
	}


	/*Messages*/
	a.Button.NewConversation.Primary, #ConversationForm label
	{
		color: var(--dimer-text) !important;
	}
	a.Button.NewConversation.Primary:hover
	{
		color: var(--main-color) !important;
	}
	ul.token-input-list, div.token-input-dropdown, div.token-input-dropdown ul li, div.token-input-dropdown ul li.token-input-dropdown-item, div.token-input-dropdown ul li.token-input-dropdown-item2
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	div.token-input-dropdown ul li:hover, div.token-input-dropdown ul li.token-input-dropdown-item:hover, div.token-input-dropdown ul li.token-input-dropdown-item2:hover
	{
		background: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	li.token-input-token
	{
		border-color: var(--hover-background) !important;
	}

	#token-input-Form_To
	{
		border: none !important;
	}

	/*Comment preview*/
	.Preview
	{
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}

sourceCode:
/* ==UserStyle==
@name LinuxMintBlog DeepDark
@namespace github.com/RaitaroH/LinuxMint_Blog-Deepdark/
@homepageURL https://github.com/RaitaroH/LinuxMint_Blog-Deepdark/
@version 1.0.5
@updateURL https://rawgit.com/RaitaroH/LinuxMint_Blog-Deepdark/master/LinuxMintBlog-DeepDark.user.css
@description LinuxMintBlog- dark theme
@author RaitaroH
@license GNU-V3.0

@preprocessor uso
@var select mainColor 'Main color' {
	"DeepDark": "#00adee",
	"BreezeDark": "#3DAEE9",
	"Vertex Dark": "#4080fb",
	"Arc Dark": "#5294E2",
	"Firefox Dark": "#5675B9",
	"Firefox57": "#4080FB",
	"Discord": "#7289DA",
	"YouTubeDark": "#E52117",
	"Mint-Y-Dark": "#9AB87C",
	"9anime": "#723f8c",
	"Black&White": "#ffffff",
	"Yellow_petrocompletions": "#FFC700",
	"Yellow2": "#FFC700",
	"Ubuntu_grey": "#EF7847",
	"Ubuntu_purple": "#EF7847",
	"Orange": "#ff6905",
	"Jisho_夜明け":  "#EF7D6C"
}

@var select mainBackground 'Main background color' {
	"DeepDark": "#111111",
	"BreezeDark": "#232629",
	"Vertex Dark": "#2B2B2C",
	"Arc Dark": "#343944",
	"Firefox Dark": "#272B35",
	"Firefox57": "#0C0C0D",
