	.tile.tile--c--w.tile--vid.has-detail.opt--t-xxs:hover, .tile.tile--c.js-tile--Top:hover, .has-tiles .tile:hover
	{
		border: none !important;
		box-shadow: none !important;
	}
	.tile.tile--c--w.tile--vid.has-detail.opt--t-xxs:hover .tile__title a, .tile.tile--c.js-tile--Top:hover .tile__title a, .has-tiles .tile:hover .tile__title a
	{
		color: var(--main-color) !important;
	}
	/*Video results*/
	.metabar__primary-text--linked
	{
		color: var(--dimer-text) !important;
	}


	/*Convertions stuff*/
	.zci--currency .frm__select, .zci--currency .frm__select::after, .zci--currency .frm__input, .zci--conversions .frm__select select, .zci--conversions .frm__select, .zci--conversions .frm__input.frm__input
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci--currency .shadow, .zci--conversions .shadow
	{
		box-shadow: var(--shadow) !important;
	}
	.zci--currency .frm__select select, .frm__select
	{
		color: var(--main-text) !important;
		background-color: var(--main-background) !important;
	}
	.zci--conversions .frm__select::after
	{
		background: transparent !important;
	}
	.zci--currency .frm__select:hover::after, .zci--conversions .frm__select:hover::after
	{
		background-color: var(--hover-background) !important;
	}


	/*About page*/
	.blk__title, .abt__title, .timeline__title, .ddh__title, .app__title, .abt__key__title, h4, .news-tile__title, .news-tile__source
	{
		color: var(--main-text) !important;
	}
	.abt__key__sub, .abt__key p
	{
		color: var(--dimer-text) !important;
	}
	.abt__key__link::before, .news-tile__link::before
	{
		background: var(--main-color) !important;
	}
	.map__title
	{
		background-color: var(--second-background) !important;
	}


	/*Bangs*/
	.bang__list-header__title, .bang__heading, .bang--about__title, .bang--about__snippet, .bang--about__text > a
	{
		color: var(--main-text) !important;
	}
	.bang__list-header__snippet, .no-visited, .bang--about__content, .bang--about__text, .bang__list-header
	{
		color: var(--dimer-text) !important;
	}
	.no-visited:hover, .bang--about__text > a:hover
	{
		color: var(--main-color) !important;
	}
	.bang__result__bang-name, .blk--alt
	{
		background-color: var(--second-background) !important;
	}
	.bang__resultset, .bang--about, .blk--foot
	{
		border-color: var(--hover-background) !important;
	}
	.blk--foot
	{
		color: var(--main-text) !important;
		background-color: var(--hover-background) !important;
	}
	.blk--hero + .blk--arr::before
	{
		border-top-color: #e37151 !important;
	}


	/*Settings*/
	.set-main .frm__label, .frm__select, .frm__select, .frm__select select
	{
		color: var(--main-text) !important;
	}
	.frm__desc.frm__desc--break-xs
	{
		color: var(--dimer-text) !important;
	}
	.frm__select select, .frm__select
	{
		border-color: var(--hover-background) !important;
		background-color: var(--second-background) !important;
	}


	/*Privacy page*/
	.blk__text
	{
		color: var(--dimer-text) !important;
	}


	/*App page*/
	.app__key__title
	{
		color: var(--main-text) !important;
	}
	.app__key p
	{
		color: var(--dimer-text) !important;
	}
	.blk--arr::before, .blk--arr--white::before
	{
		border-top-color: var(--main-background) !important;
	}

	/*Code response*/
	.module__text pre
	{
		background: var(--second-background) !important;
	}
	.prettyprint .com, .module__title__sub, pre, tt, code
	{
		color: var(--dimer-text) !important;
	}
	.prettyprint .lit
	{
		color: var(--main-color) !important;
	}


	/*The calculator*/
	.zci--calculator .tile__calc .tile__display, .zci--calculator .tile__calc .tile__history, .zci--calculator .tile__calc .tile__ctrl--ops, .zci--calculator .tile__calc .tile__tab__sci .tile__ctrl__btn, .zci--calculator .tile__calc .tile__ctrl__toggle
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci--calculator .tile__calc .tile__past-calc, .zci--calculator .tile__calc .tile__ctrl__btn
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci--calculator .tile__calc .tile__ctrl--important
	{
		background-color: var(--main-color) !important;
		border-color: var(--main-color) !important;
	}
	.zci--calculator .tile__calc .tile__display, .zci--calculator .tile__calc .tile__history, .zci--calculator .tile__calc .tile__ctrl--ops, .zci--calculator .tile__calc .tile__tab__sci .tile__ctrl__btn, .zci--calculator .tile__calc .tile__ctrl__toggle, .zci--calculator .tile__calc .tile__ctrl__btn, .zci--calculator .tile__calc .tile__past-calc, .zci--calculator .tile__calc .tile__display__main, .zci--calculator .tile__calc .tile__past-result
	{
		color: var(--main-text) !important;
	}
	.zci--calculator .tile__calc .tile__ctrl__btn:hover, .zci--calculator .tile__calc .tile__ctrl__toggle:hover
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci--calculator .tile__calc .tile__display__aside, .zci--calculator .tile__calc .tile__past-formula
	{
		color: var(--dimer-text) !important;
	}
	.zci--calculator .tile__ctrl__toggle-slider
	{
		background-color: var(--dimer-text) !important;
	}
	.zci--calculator .tile__calc__col
	{
		border-color: var(--hover-background) !important;
	}
	.zci--calculator .tile__calc .tile__display:hover
	{
		box-shadow: inset -1px -1px 0px var(--main-color), inset 1px 1px 0px var(--main-color) !important;
	}
	.zci--calculator .tile__calc .tile__display
	{
		box-shadow: inset -1px -1px 0px var(--hover-background), inset 1px 1px 0px var(--hover-background) !important;
	}


	/*2048 game*/
	.zci--game2048 .game2048__area
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci--game2048 .cell
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci--game2048 .game2048__new_game
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	.zci--game2048 .game2048__points
	{
		color: var(--main-text) !important;
	}
	.zci--game2048 .game2048__help
	{
		color: var(--dimer-text) !important;
	}
	.zci--game2048 .game2048__area:focus
	{
		box-shadow: none !important;
		-webkit-box-shadow: none !important;
		-moz-box-shadow: none !important;
	}

	/*Color picker*/
	#color_picker_container input
	{
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	#color_picker_container #palette_select
	{
		color: var(--main-text) !important;
	}

	/*News*/
	.module--news__title, .module--news__body__title
	{
		color: var(--main-text) !important;
	}
	.module--news__title:hover, .module--news__body__title:hover, .module--news__item:hover .module--news__body__title
	{
		color: var(--main-color) !important;
	}
	.feedback-prompt, .module--news__footer
	{
		color: var(--dimer-text) !important;
	}
	.module--news__item
	{
		border-color: var(--hover-background) !important;
	}
	.module--news__left, .module--news__right
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.module--news__left:hover, .module--news__right:hover
	{
		color: var(--main-color) !important;
	}

	/*Map*/
	.module__section
	{
		border-color: var(--hover-background) !important;
	}
	.module__section p
	{
		color: var(--dimer-text) !important;
	}

/*Issues in html mode*/
	.search__input, .search__input--adv
	{
		color: var(--dimer-text) !important;
	}
	.header.header--html
	{
		border-color: var(--hover-background) !important;
	}
	.btn--alt:hover
	{
		color: var(--main-color) !important;
	}
	.logo_homepage
	{
		background-image: url(https://raw.githubusercontent.com/RaitaroH/DuckDuckGo-DeepDark/master/Images/BigLogo.png) !important;
	}
	.header__logo-wrap
	{
		background-image: url(https://raw.githubusercontent.com/RaitaroH/DuckDuckGo-DeepDark/master/Images/logo.png) !important;
	}
	.search, .search--adv
	{
		background-color: var(--second-background) !important;
		border-color: var(--second-background) !important;
	}
	.zci-wrapper
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci
	{
		color: var(--dimer-text) !important;
	}

sourceCode:
/* ==UserStyle==
@name Vidme DeepDark
@namespace github.com/RaitaroH/Vidme-DeepDark
@homepageURL https://github.com/RaitaroH/Vidme-DeepDark
@version 1.2.4
@updateURL https://rawgit.com/RaitaroH/Vidme-DeepDark/master/VidmeDeepDark.user.css
@description Vidme dark theme
@author RaitaroH
@license GNU-V3.0

@preprocessor uso
@var select mainColor 'Main color' {
	"DeepDark": "#00adee",
	"BreezeDark": "#3DAEE9",
	"Vertex Dark": "#4080fb",
	"Arc Dark": "#5294E2",
	"Firefox Dark": "#5675B9",
	"Firefox57": "#4080FB",
	"Discord": "#7289DA",
	"YouTubeDark": "#E52117",
	"Mint-Y-Dark": "#9AB87C",
	"9anime": "#723f8c",
	"Black&White": "#ffffff",
	"Yellow_petrocompletions": "#FFC700",
	"Yellow2": "#FFC700",
	"Ubuntu_grey": "#EF7847",
	"Ubuntu_purple": "#EF7847",
	"Orange": "#ff6905",
	"Jisho_夜明け":  "#EF7D6C"
}

@var select mainBackground 'Main background color' {
	"DeepDark": "#111111",
	"BreezeDark": "#232629",
	"Vertex Dark": "#2B2B2C",
	"Arc Dark": "#343944",
	"Firefox Dark": "#272B35",
	"Firefox57": "#0C0C0D",
	"Discord": "#1E2124",
	"YouTubeDark": "#111111",
	"Mint-Y-Dark": "#2F2F2F",
	"9anime": "#0B0A0D",
	"Black&White": "#000000",
	"Yellow_petrocompletions": "#141414",
	"Yellow2": "#0a0800",
	"Ubuntu_grey": "#312D2A",
	"Ubuntu_purple": "#2C071A",
	"Orange": "#0a0400",
	"Jisho_夜明け":  "#332222"
}

@var select secondBackground 'Second background color' {
	"DeepDark": "#181818",
	"BreezeDark": "#2a2e32",
	"Vertex Dark": "#353638",
	"Arc Dark": "#383C4A",
	"Firefox Dark": "#181D20",
	"Firefox57": "#252526",
	"Discord": "#2F3136",
	"YouTubeDark": "#232323",
	"Mint-Y-Dark": "#383838",
	"9anime": "#17151C",
	"Black&White": "#0a0a0a",
	"Yellow_petrocompletions": "#202222",
	"Yellow2": "#0c0a04",
	"Ubuntu_grey": "#3D3C38",
	"Ubuntu_purple": "#430B28",
	"Orange": "#0e0702",
	"Jisho_夜明け":  "#2A1B1B"
}

@var select hoverBackground 'Hover background color' {
	"DeepDark": "#232323",
	"BreezeDark": "#31363b",
	"Vertex Dark": "#515254",
	"Arc Dark": "#414A59",
	"Firefox Dark": "#353A44",
	"Firefox57": "#323234",
	"Discord": "#484B51",
	"YouTubeDark": "#343434",
	"Mint-Y-Dark": "#404040",
	"9anime": "#1E1C25",
	"Black&White": "#0f0f0f",
	"Yellow_petrocompletions": "#353838",
	"Yellow2": "#0f0d05",
	"Ubuntu_grey": "#59564D",
	"Ubuntu_purple": "#520D30",
	"Orange": "#110903",
	"Jisho_夜明け":  "#863B2F"
}

@var select mainText 'Main text color' {
	"DeepDark": "#eff0f1",
	"BreezeDark": "#eff0f1",
	"Vertex Dark": "#F3F3F5",
	"Arc Dark": "#C1C8D1",
	"Firefox Dark": "#E3EEF9",
	"Firefox57": "#F9F9FA",
	"Discord": "#FFFFFF",
	"YouTubeDark": "#E1E1E1",
	"Mint-Y-Dark": "#FFFFFF",
	"9anime": "#B4B4B4",
	"Black&White": "#b4b4b4",
	"Yellow_petrocompletions": "#EFF0F1",
	"Yellow2": "#fffdf5",
	"Ubuntu_grey": "#F2F1EF",
	"Ubuntu_purple": "#F2F1EF",
	"Orange": "#fff9f5",
	"Jisho_夜明け":  "#EFB26C"
}

@var select dimerText 'Secondary text color' {
	"DeepDark": "#CCCCCC",
	"BreezeDark": "#bdc3c7",
	"Vertex Dark": "#AEAFB0",
	"Arc Dark": "#B3BAC5",
	"Firefox Dark": "#606376",
	"Firefox57": "#D0D0D0",
	"Discord": "#ADA8AA",
	"YouTubeDark": "#7F7F7F",
	"Mint-Y-Dark": "#D5DADA",
	"9anime": "#747474",
	"Black&White": "#828282",
	"Yellow_petrocompletions": "#9F9999",
	"Yellow2": "#fff8e1",
	"Ubuntu_grey": "#E6E5E3",
	"Ubuntu_purple": "#E6E5E3",
	"Orange": "#ffede1",
	"Jisho_夜明け":  "#986E3F"
}

==/UserStyle== */

@-moz-document domain("vid.me") {

/*Theme made by RaitaroH @https://github.com/RaitaroH/Vidme-DeepDark*/

/*GNU General Public License v3.0*/

/*1.2.4*/

	/*Main color variables*/
	:root
	{
		/*User colors*/
		--main-color: /*[[mainColor]]*/;
		--main-background: /*[[mainBackground]]*/;
		--second-background: /*[[secondBackground]]*/;
		--hover-background: /*[[hoverBackground]]*/;
		--main-text: /*[[mainText]]*/;
		--dimer-text: /*[[dimerText]]*/;

		/*DeepDark colors*/
		/*
		--main-color: #00adee;
		--main-background: #111111;
		--second-background: #181818;
		--hover-background: #232323;
		--main-text: #eff0f1;
		--dimer-text: #CCCCCC;
		*/

		/*KDE colors*/
		/*
		--main-color: #3DAEE9;
		--main-background: #232629;
		--second-background: #2a2e32;
		--hover-background: #31363b;
		--main-text: #eff0f1;
		--dimer-text: #bdc3c7;
		*/

		/*Vertex Dark colors*/
		/*
		--main-color: #4080fb;
		--main-background: #2B2B2C;
		--second-background: #353638;
		--hover-background: #515254;
		--main-text: #F3F3F5;
		--dimer-text: #AEAFB0;
		*/

		/*Arc Dark colors*/
		/*
		--main-color: #5294E2;
		--main-background: #343944;
		--second-background: #383C4A;
		--hover-background: #414A59;
		--main-text: #C1C8D1;
		--dimer-text: #B3BAC5;
		*/

		/*Firefox Dark colors*/
		/*
		--main-color: #5675B9;
		--main-background: #272B35;
		--second-background: #181D20;
		--hover-background: #353A44;
		--main-text: #E3EEF9;
		--dimer-text: #606376;
		*/

		/*Firefox57 Dark colors*/
		/*
		--main-color: #4080FB;
		--main-background: #0C0C0D;
		--second-background: #252526;
		--hover-background: #323234;
		--main-text: #F9F9FA;
		--dimer-text: #D0D0D0;
		*/

		/*Discord colors*/
		/*
		--main-color: #7289DA;
		--main-background: #1E2124;
		--second-background: #2F3136;
		--hover-background: #484B51;
		--main-text: #FFFFFF;
		--dimer-text: #ADA8AA;
		*/

		/*YouTube dark colors*/
		/*
		--main-color: #E52117;
		--main-background: #111111;
		--second-background: #232323;
		--hover-background: #343434;
		--main-text: #E1E1E1;
		--dimer-text: #7F7F7F;
		*/

		/*Mint-Y-Dark colors*/
		/*
		--main-color: #9AB87C;
		--main-background: #2F2F2F;
		--second-background: #383838;
		--hover-background: #404040;
		--main-text: #FFFFFF;
		--dimer-text: #D5DADA;
		*/

		/*9anime colors*/
		/*
		--main-color: #723f8c;
		--main-background: #0B0A0D;
		--second-background: #17151C;
		--hover-background: #1E1C25;
		--main-text: #B4B4B4;
		--dimer-text: #747474;
		*/

		/*Black and white*/
		/*
		--main-color: #ffffff;
		--main-background: #000000;
		--second-background: #0a0a0a;
		--hover-background: #0f0f0f;
		--main-text: #b4b4b4;
		--dimer-text: #828282;
		*/

		/*Yellow (colors from petrocompletions)*/
		/*
		--main-color: #FFC700;
		--main-background: #141414;
		--second-background: #202222;
		--hover-background: #353838;
		--main-text: #EFF0F1;
		--dimer-text: #9F9999;
		*/

		/*Yellow 2*/
		/*
		--main-color: #ffc700;
		--main-background: #0a0800;
		--second-background: #0c0a04;
		--hover-background: #0f0d05;
		--main-text: #fffdf5;
		--dimer-text: #fff8e1;
		*/

		/*Ubuntu grey colors*/
		/*
		--main-color: #EF7847;
		--main-background: #312D2A;
		--second-background: #3D3C38;
		--hover-background: #59564D;
		--main-text: #F2F1EF;
		--dimer-text: #E6E5E3;
		*/

		/*Ubuntu purple colors*/
		/*
		--main-color: #EF7847;
		--main-background: #2C071A;
		--second-background: #430B28;
		--hover-background: #520D30;
		--main-text: #F2F1EF;
		--dimer-text: #E6E5E3;
		*/

		/*Orange*/
		/*
		--main-color: #ff6905;
		--main-background: #0a0400;
		--second-background: #0e0702;
		--hover-background: #110903;
		--main-text: #fff9f5;
		--dimer-text: #ffede1;
		*/

		/*Jisho 夜明け colors https://userstyles.org/styles/115621/jisho*/
		/*
		--main-color: #EF7D6C;
		--main-background: #332222;
		--second-background: #2A1B1B;
		--hover-background: #863B2F;
		--main-text: #EFB26C;
		--dimer-text: #986E3F;
		*/
	}


	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}


	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}


	/**Removing text decorations*/
	*
	{
		text-decoration: none !important;
	}


	/*Body*/
	body.pg-video-view, .content_wrapper, body, .main
	{
		background-color: var(--main-background) !important;
	}


	/*Header*/
	.header.opaque
	{
		background-color: var(--second-background) !important;
	}
	.navbar-inverse .navbar-collapse, .navbar-inverse .navbar-form
	{
		border-color: var(--hover-background) !important;
	}
	/*Logo*/
	.header a.logo .logo-black, .header_gutter .logo-gutter
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Vidme-DeepDark/master/Images/logo-white.png") !important;
	}
	.header a.logo > span.logo-black, .header a.logo > span.logo-white, .header_gutter .logo-gutter
	{
		opacity: .9;
	}
	.header a.logo > span.logo-black:hover, .header a.logo > span.logo-white:hover, .header_gutter .logo-gutter:hover
	{
		opacity: 1;
	}
	/*Sidebar button*/
	.header.opaque .menu-hamburger > a
	{
		opacity: .8 !important;
		filter: invert(100%) !important;
  	}
	.header.opaque .menu-hamburger > a:hover
	{
		opacity: 1 !important;
  	}
	/*Text in the header*/
	.header.opaque .main-links > li > a, .header.opaque .menu > li > a
	{
		color: var(--main-text) !important;
	}
	.header.opaque .main-links > li > a:hover, .header.opaque .menu > li > a:hover, .header .main-links > li > a:hover:hover, .header .menu > li > a:hover
	{
		background-color: transparent !important;
		color: var(--main-color) !important;
	}
	/*Upload button*/
