		color: var(--dimer-text) !important;
	}
	.users_list .user_videos:hover, .users_list .user_followers:hover, .users_list .user_views:hover, .users_list .user_tips:hover, .users_list .user_videos:hover .fa, .users_list .user_followers:hover .fa, .users_list .user_views:hover .fa, .users_list .user_tips:hover .fa
	{
		color: var(--main-color) !important;
	}
	.tabs li.active a
	{
		border-color: var(--main-color) !important;
	}
	/*User results*/
	.users_list .user_photo > a
	{
		background-color: var(--hover-background) !important;
	}
	/*See more*/
	.users_list_more a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.users_list_more:hover a
	{
		opacity: 1 !important;
	}

	/*Following*/
	h1
	{
		color: var(--main-text) !important;
	}
	.sign-up-link, .sign-in-link
	{
		color: var(--main-text) !important;
	}
	.sign-up-link:hover, .sign-in-link:hover
	{
		color: var(--main-color) !important;
	}
	#user_view .user_header .user_actions a.user_follow.following .txt
	{
		color: var(--main-text) !important;
	}

	/*Sign up*/
	.modal-content
	{
		background: var(--second-background) !important;
	}
	.modal-header
	{
		border-color: var(--hover-background) !important;
	}
	.modal .modal-title
	{
		color: var(--main-text) !important;
	}
	.modal-body .help, .social-auth .social-auth-or, input[type="text"], .form-control, .modal .modal-footer a:not(.btn)
	{
		color: var(--dimer-text) !important;
	}
	.modal .form-control
	{
		background-color: var(--hover-background) !important;
		border-color:  var(--hover-background) !important;
	}
	.modal .form-control:hover, .modal .form-control:active, .modal .form-control:focus
	{
		border-color:  var(--main-color) !important;
	}
	.modal .modal-footer .btn[type="submit"]
	{
		background: var(--main-color) !important;
	}
	.modal .modal-footer .btn[type="submit"]:hover
	{
		background: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	.modal .modal-footer a:not(.btn):hover
	{
		border-color:  var(--main-color) !important;
	}


	/*Notifications*/
	.header .label-notifications, .header .label-follow-unread
	{
		background-color: var(--main-color) !important;
	}
	.header .menu-notifications .notify-badge-anchor svg
	{
		opacity: .9 !important;
	}
	.notifications_list li, .notifications_list li a:not(.btn)
	{
		color: var(--main-text) !important;
	}
	.notification-dropdown, .notifications_list li + li
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	#user_view .user_header .user_actions a.user_notify.notified, #video_view .video_author_wrapper .btn.notified, #video_view .video_author_wrapper .btn.user_notify
	{
		background-color: transparent !important;
		border-color: transparent !important;
	}
	#user_view .user_header .user_actions a.user_notify.notified i.fa-bell, #video_view .video_author_wrapper .btn.user_notify.notified i.fa-bell, #video_view .video_author_wrapper .btn.user_notify:hover, .notifications_list li a:not(.btn):hover
	{
		color: var(--main-color) !important;
	}
	.header.opaque .menu-notifications .notify-badge-anchor svg:hover
	{
		fill: var(--main-color) !important;
	}

	/*Scrolling buttons*/
	.pg-index .videos_shelf .scroll > a
	{
		background-color: var(--hover-background) !important;
	}
	.pg-index .videos_shelf .scroll > a img
	{
		filter: invert(100%);
		opacity: .5 !important;
	}


	/*Tiping*/
	.tip-modal .price-breakdown .row .col
	{
		color: var(--main-text) !important;
	}


	/*Watch later*/
	#album_view .album_video_add > a, #album_view .album_owner .album_owner_username
	{
		color: var(--main-text) !important;
	}
	#album_view .album_info > li
	{
		color: var(--dimer-text) !important;
	}
	#album_view .album_owner .album_owner_username:hover
	{
		color: var(--main-color) !important;
	}
	/*cancel button*/
	.modal .modal-footer .btn + .btn
	{
		background-color: transparent !important;
	}


	/*Watched videos*/
	.videos_list > li.watched::before
	{
		font-size: 14px;
		/*
		padding: 4px 6px;
		background-color: rgba(0,0,0,0.5);
		font-weight: bold;
		margin-bottom: 119px;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0;
		align-items: left;
		justify-content: left;
		margin-right: 6px;
		*/
	}


	/*Add to album*/
	.list-group-item, .album-video-add-modal .album_info.list-group
	{
		border-color: var(--hover-background) !important;
	}
	.album-video-add-modal .album_info.list-group li
	{
		color: var(--dimer-text) !important;
	}
	.album-video-add-modal .album_info.list-group li:hover
	{
		background-color: var(--hover-background) !important;
	}


	/*Edit album*/
	.album-edit-videos-modal fieldset
	{
		background-color: var(--second-background) !important;
		border-color: transparent !important;
	}


	/*Own channel bio*/
	#user_view .user_info .alert, #user_view .alert
	{
		color: var(--dimer-text) !important;
	}
	#user_view .user_info .alert:hover
	{
		color: var(--main-color) !important;
	}
	/*Hover for friends*/
	#user_view .user_friends .friend_username:hover
	{
		color: var(--main-color) !important;
	}
	#user_view .user_friends .friend_username
	{
		color: var(--main-text) !important;
	}


	/*Settings*/
	#settings .tabs, #settings > .inner
	{
		background-color: var(--second-background) !important;
	}
	#settings .tabs li.active > a, #settings .tabs li > a:hover, .alert-warning
	{
		background-color: var(--hover-background) !important;
	}
	#settings .tabs li.headline, #settings .form-group > label, .manage_page .manage_filters > span
	{
		color: var(--main-text) !important;
	}
	#settings .form-group > label .desc, #settings .form-alternative a, .alert-wrapper .alert, .manage_page .manage_filters .btn-xs, .manage_page .quota
	{
		color: var(--dimer-text) !important;
	}
	.alert-success, #settings .settings_items_add > a:hover, #settings .settings_blocklist a:hover, #settings .settings_friends a:hover, div.form-group:nth-child(14) > label:nth-child(1) > div:nth-child(1) > a:nth-child(1), #settings .form-alternative a:hover, .help > a:nth-child(1), .manage_page .manage_filters .btn-xs:hover
	{
		color: var(--main-color) !important;
		background-color: transparent !important;
	}
	#settings .form-group
	{
		border-color: var(--hover-background) !important;
	}
	#settings .form-control:hover,#settings .form-control:active, #settings .form-control:focus, #settings .settings_items_add > a:hover
	{
		border-color: var(--main-color) !important;
	}
	/*Checked buttons*/
	.form-checkbox input:checked + .form-icon, .form-radio input:checked + .form-icon
	{
		border-color: var(--main-color) !important;
		background-color: transparent !important;
	}
	.form-switch input[type="checkbox"]:checked + .form-icon
	{
		background-color: var(--main-color) !important;
	}


	/*Messages*/
	.messages-fragment, ul.threads > li.active-thread
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	ul.threads, #messages .thread-header, .message-composer, .message-composer-submit
	{
		border-color: var(--hover-background) !important;
	}
	#messages .thread-headline, ul.threads .message-users
	{
		color: var(--main-text) !important;
	}
	li.message .message-text, ul.threads .message .message-text, #messages .thread-header a, .message-composer > textarea
	{
		color: var(--dimer-text) !important;
	}
	li.message .message-author a, #messages .thread-header a:hover, #messages .thread-header a:hover .fa
	{
		color: var(--main-color) !important;
	}
	.btn-success, .btn-success:hover, .btn-success:active, .btn-success:focus, .btn-success.disabled
	{
		background-color: var(--main-color) !important;
		opacity: .9;
	}
	.btn-success:hover, .btn-success:hover, .btn-success:active:hover, .btn-success:focus:hover, .btn-success.disabled:hover
	{
		opacity: 1;
	}


	/*About vidme and such*/
	.about-body
	{
		background-color: var(--second-background) !important;
	}
	.about-wrapper h2, .about-body ol li strong, label, .about-body b
	{
		color: var(--main-text) !important;
	}
	.about-body p, .about-body ol, .about-body ul.bulleted, .about-body .table, .pg-help-widgets .widget_follow_code textarea, .alert-info
	{
		color: var(--dimer-text) !important;
	}
	p.answer:nth-child(4) > a, p.question > a, p.answer > a, .about-body > ol > li > a, .about-body > p > a, ul.answer > li > a, div.brand-logos > div > span > a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	p.answer:nth-child(4) > a:hover, p.question > a:hover, p.answer > a:hover, .about-body > ol > li > a:hover, .about-body > p > a:hover, ul.answer > li > a:hover, div.brand-logos > div > span > a:hover
	{
		opacity: 1;
	}
	ul.answer:nth-child(3) > li:nth-child(11) > span:nth-child(1)
	{
		filter: grayscale(100%) !important;
	}
	.btn-info
	{
		background-color: transparent !important;
	}
	#earn .capture-email .input-wrapper
	{
		background-color: var(--hover-background) !important;
	}
	.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td, .pg-help-widgets .widget_follow_code textarea
	{
		border-color: var(--hover-background) !important;
	}
	.user_follow_button
	{
		background-color: transparent !important;
	}
	.vidme-widget > iframe:nth-child(1)
	{
		border-color: transparent !important;
	}


	/*Title hover*/
	.videos_list > li:hover .thumb_title_label, #video_view .video_suggestions li:hover .title > a, #index .featured_users ul > li:hover .featured_users_label, .albums_list > li:hover .thumb_title_label
	{
		color: var(--main-color) !important;
	}


	/*Rethemed channel page*/
	.dashboard .section, .videos_list > li, .albums_list > li
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.dashboard_left, .dashboard .section + .section, #user_view .levels, #user_view .tipboard, .dashboard_main, #user_view .tabs_wrapper
	{
		border-color: var(--hover-background) !important;
	}
	#user_view .levels, #user_view .tipboard
	{
		color: var(--dimer-text) !important;
	}
	#user_view .levels .level_title, .albums_list > li .thumb_title_label
	{
		color: var(--main-text) !important;
	}
	#user_view .tipboard_sum
	{
		background-color: transparent !important;
		color: var(--main-color) !important;
	}
	/*New sub and follow buttons*/
	#user_view .user_actions a.user_subscribe, #user_view .user_actions a.user_follow.following, #user_view .levels .level_button > a, .dashboard .prompt_follow ul .follow, .dashboard .prompt_follow .discover-button, #user_view .user_actions a.user_follow
	{
		background: transparent !important;
		border: 1px solid var(--main-color) !important;
		color: var(--main-color) !important;
		opacity: .9;
		line-height: 20px;
	}
	#user_view .user_actions a.user_subscribe:hover, #user_view .user_actions a.user_follow.following:hover, #user_view .levels .level_button > a:hover, .dashboard .prompt_follow ul .follow:hover, .dashboard .prompt_follow .discover-button:hover, #user_view .user_actions a.user_follow:hover
	{
		opacity: 1 !important;
		background-color: transparent !important;
	}
	.dashboard .prompt_follow ul .follow
	{
		text-align: center !important;
	}
	.user_follow .fa-check, .user_subscribe .fa-check
	{
		color: var(--main-color) !important;
	}
	#user_view .user_vip, #user_view .user_featured
	{
		filter: grayscale(100%) !important;
	}
	#user_view .user_actions a.btn img
	{
		filter: invert(100%);
		opacity: .6 !important;
	}
	#user_view .user_actions a.btn img:hover
	{
		opacity: 1 !important;
	}
	.videos_list .thumb_action > a
	{
		border-color: var(--main-text) !important;
	}
	.videos_list .thumb_action > a:hover
	{
		border-color: var(--main-color) !important;
	}
	#user_view .user_actions a.user_notify.notified i.fa-bell
	{
		color: var(--main-color) !important;
	}
	#message-compose-modal .message-to
	{
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	#user_view .user_bio > a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	#user_view .user_bio > a:hover
	{
		opacity: 1 !important;
	}


	/*Retheme video page*/
	#video_view .video_info, #video_view .gutter, #video_view .video_likes, #video_view .video_tips, #video_view .comments
	{
		border-color: var(--hover-background) !important;
	}


	/*My feed*/
	.dashboard_title
	{
		background-color: var(--second-background) !important;
	}
	.dashboard_title, .dashboard_right
	{
		border-color: var(--hover-background) !important;
	}
	.dashboard_title, .dashboard .dashboard_headline, .dashboard .prompt_follow ul .username, .dashboard .dashboard-menu li a, .dashboard_title .dashboard_sort .btn
	{
		color: var(--main-text) !important;
	}
	.dashboard p, .dashboard ol, .dashboard p a, .dashboard ol a
	{
		color: var(--dimer-text) !important;
	}
	.dashboard p a:hover, .dashboard ol a:hover, .dashboard .prompt_follow ul .username:hover, .dashboard .dashboard-menu li a:hover, .dashboard_title .dashboard_sort .btn:hover
	{
		color: var(--main-color) !important;
	}



	/*My watched later*/
	.dashboard .inner .alert-wrapper
	{
		background-color: var(--hover-background) !important;
	}
	#album_view .album_video_add > a, #album_view .album_edit i
	{
		color: var(--main-color) !important;
	}
	#album_view .videos_list
	{
		border-color: var(--hover-background) !important;
	}


	/*Browse creators*/
	.dashboard .users_list
	{
		background-color: var(--second-background) !important;
	}


	/*Notifications*/
	.notifications_list li.unread
	{
		background-color: var(--second-background) !important;
	}
	.users_list .user_notify
	{
		background-color: transparent !important;
	}
	.users_list .user_notify.notified i.fa-bell, .users_list .user_follow.following, .users_list .user_notify.notified
	{
		color: var(--main-color) !important;
	}


	/*My videos*/
	#settings .tabs_wrapper, #settings > .inner
	{
		border-color: var(--hover-background) !important;
	}
	.dashboard .dashboard-menu
	{
		background-color: var(--second-background) !important;
	}


	/*Upload*/
	#uploader-modal .step1 > .upload-option
	{
		background-color: var(--hover-background) !important;
	}
	#uploader-modal .step1 > * span
	{
		color: var(--dimer-text) !important;
	}
	#uploader-modal .step1 > :hover span, .modal .modal-content a
	{
		color: var(--main-color) !important;
	}
	/*Upload videos button*/
	.dashboard .prompt_follow .discover-button, .dashboard .prompt_upload .upload-button
	{
		border-color: var(--main-color) !important;
		color: var(--main-color) !important;
		opacity: .9;
	}
	.dashboard .prompt_follow .discover-button:hover, .dashboard .prompt_upload .upload-button:hover
	{
		border-color: var(--main-color) !important;
		color: var(--main-color) !important;
		opacity: 1;
	}


	/*Post comment*/
	.comments_composer_submit .btn
	{
		border: 1px solid var(--main-color) !important;
		color: var(--main-color) !important;
	}


	/*Category hover*/
	#video_view .video_category:hover .fa, .videos_list > li .thumb_unlisted i, .videos_list > li .thumb_channel i
	{
		color: var(--main-color) !important;
	}


	/*No search results found*/
	#search .alert-wrapper
	{
		background-color: var(--second-background) !important;
	}


	/*Add video to watch later*/
	.modal .modal-desc
	{
		color: var(--dimer-text) !important;
	}
	.modal .form-group .input-group-addon
	{
		background-color: var(--hover-background) !important;
	}
	.album-add-videos-modal ul.album-add-video-list
	{
		border-color: var(--hover-background) !important;
	}
	.album-add-videos-modal .thumb + div
	{
		color: var(--main-text) !important;
	}
	.album-add-videos-modal ul.album-add-video-list li:hover
	{
		background-color: var(--hover-background) !important;
	}
	.album-add-videos-modal ul.album-add-video-list li:hover .thumb + div, .alert-warning
	{
		color: var(--main-color) !important;
	}
	.close
	{
		color: var(--main-text) !important;
		opacity: 1 !important;
		text-shadow: none !important;
	}
	.close:hover
	{
		color: var(--main-color) !important;
		opacity: 1 !important;
		text-shadow: none !important;
	}
	.btn-default
	{
		background-color: transparent !important;
	}


	/*Comments page*/
	.comments_list .comment_video_title a
	{
		color: var(--dimer-text) !important;
	}
	.comments_list .comment_video_title a:hover
	{
		color: var(--main-color) !important;
	}
	.comments_list .comment_body a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.comments_list .comment_body a:hover
	{
		color: var(--main-color) !important;
		opacity: 1;
	}

	/*Share video and report video*/
	.modal .modal-subtitle, .embed-share-modal textarea.embed-code, .message
	{
		color: var(--dimer-text) !important;
	}
	.embed-share-modal textarea.embed-code, .embed-share-modal .timewidget-url.timewidget-url-wrapper
	{
		border-color: var(--hover-background) !important;
	}
	.btn-danger
	{
		color: #d43f3a !important;
	}

sourceCode:
/* ==UserStyle==
@name KissAnime DeepDark
@namespace github.com/RaitaroH/KissAnime-DeepDark
@homepageURL https://github.com/RaitaroH/KissAnime-DeepDark
@version 2.1.3
@updateURL https://rawgit.com/RaitaroH/KissAnime-DeepDark/master/KissAnimeDeepDark.user.css
@description KissAnime dark theme
@author RaitaroH
@license GNU-V3.0

@preprocessor uso
@var select mainColor 'Main color' {
	"DeepDark": "#00adee",
	"BreezeDark": "#3DAEE9",
	"Vertex Dark": "#4080fb",
	"Arc Dark": "#5294E2",
	"Firefox Dark": "#5675B9",
	"Firefox57": "#4080FB",
	"Discord": "#7289DA",
	"YouTubeDark": "#E52117",
	"Mint-Y-Dark": "#9AB87C",
	"9anime": "#723f8c",
	"Black&White": "#ffffff",
	"Yellow_petrocompletions": "#FFC700",
	"Yellow2": "#FFC700",
	"Ubuntu_grey": "#EF7847",
	"Ubuntu_purple": "#EF7847",
	"Orange": "#ff6905",
	"Jisho_夜明け":  "#EF7D6C"
}

@var select mainBackground 'Main background color' {
	"DeepDark": "#111111",
	"BreezeDark": "#232629",
	"Vertex Dark": "#2B2B2C",
	"Arc Dark": "#343944",
	"Firefox Dark": "#272B35",
	"Firefox57": "#0C0C0D",
	"Discord": "#1E2124",
	"YouTubeDark": "#111111",
	"Mint-Y-Dark": "#2F2F2F",
	"9anime": "#0B0A0D",
	"Black&White": "#000000",
	"Yellow_petrocompletions": "#141414",
	"Yellow2": "#0a0800",
	"Ubuntu_grey": "#312D2A",
	"Ubuntu_purple": "#2C071A",
	"Orange": "#0a0400",
	"Jisho_夜明け":  "#332222"
}

@var select secondBackground 'Second background color' {
	"DeepDark": "#181818",
	"BreezeDark": "#2a2e32",
	"Vertex Dark": "#353638",
	"Arc Dark": "#383C4A",
	"Firefox Dark": "#181D20",
	"Firefox57": "#252526",
	"Discord": "#2F3136",
	"YouTubeDark": "#232323",
	"Mint-Y-Dark": "#383838",
	"9anime": "#17151C",
	"Black&White": "#0a0a0a",
	"Yellow_petrocompletions": "#202222",
	"Yellow2": "#0c0a04",
	"Ubuntu_grey": "#3D3C38",
	"Ubuntu_purple": "#430B28",
	"Orange": "#0e0702",
	"Jisho_夜明け":  "#2A1B1B"
}

@var select hoverBackground 'Hover background color' {
	"DeepDark": "#232323",
	"BreezeDark": "#31363b",
	"Vertex Dark": "#515254",
	"Arc Dark": "#414A59",
	"Firefox Dark": "#353A44",
	"Firefox57": "#323234",
	"Discord": "#484B51",
	"YouTubeDark": "#343434",
	"Mint-Y-Dark": "#404040",
	"9anime": "#1E1C25",
	"Black&White": "#0f0f0f",
