	}


	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}

	html, body
	{
		background: var(--main-background) !important;
		color: var(--main-text) !important;
	}

	/*Hiding crap*/
	/*Ads*/
	#divFloatLeft, #tab-trending > div:nth-child(1), #divAds2, #divAds1, #divAds, #divFloatRight, #adsIfrme6, div.divCloseBut, #adsIfrme0, #adsIfrme1, #adsIfrme2, #adsIfrme3, #adsIfrme4, #adsIfrme5, #adsIfrme6, #adsIfrme7, #adsIfrme8, #adsIfrme10, #videoAd, .videoAdClose, [id*="BB_S"]
	{
		display: none !important;
	}


	/*Change logo section*/
	#head h1 a.logo {
		padding-bottom: 10px !important;
		width: 600px !important;
		margin-top: 0px !important;
		margin-left: 20px !important;
		z-index: 9999 !important;
	}
	#headnav
	{
		background: transparent !important;
		margin: 0px !important;
	}
	#head h1
	{
		background: url("https://raw.githubusercontent.com/RaitaroH/KissAnime-DeepDark/master/Images/logo.png") !important;
		width: 80% !important;
		margin: -10px 10px 0px 0px !important;
	}


	/*Login*/
	#topHolderBox
	{
		color: var(--main-text) !important;
		background: var(--second-background) !important;
	}
	#topHolderBox > img:nth-child(1)
	{
		display: none;
	}
	#topHolderBox > a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	#topHolderBox > a:hover
	{
		opacity: 1 !important;
	}
	#head > h3:nth-child(1) > div:nth-child(1)
	{
		margin: -40px 46px 0px 0px !important;
	}


	/* Serch section */
	#search input.button, #formSearch > div:nth-child(4) > a:nth-child(1) > img:nth-child(1)
		{
		display: none;
		}
	#search
	{
		position: absolute !important;
		top: 51px !important;
		right: 250px !important;
		visibility: visible !important;
		z-index: 999 !important;
	}
	#formSearch > div:nth-child(4)
	{
		position: absolute !important;
		top: 5px !important;
		right: -126px !important;
		visibility: visible !important;
		z-index: 999 !important;
		margin-right: 25px !important;
	}
	#keyword
	{
		width: 165px !important;
	}
	#result_box a span, #formSearch > div:nth-child(4) > a:nth-child(1):hover
	{
		color: var(--main-color) !important;
	}
	#formSearch > div:nth-child(4) > a:nth-child(1)
	{
		color: var(--dimer-text) !important;
	}
		input.text:focus
	{
		width: 99% !important;
		transition: all 0.3s ease-in-out 0s !important;
	}
	input.text, .txtSignUp
	{
		margin: 0px !important;
		color: var(--dimer-text) !important;
		background: var(--second-background) !important;
		box-shadow: 0px 0px 10px var(--main-background) !important;
		transition: all 0.3s ease-in-out 0s !important;
		border-color: var(--hover-background) !important;
	}
	input.text:focus, input.text:active, input.text:hover, .txtSignUp:hover, .txtSignUp:active, .txtSignUp:focus
	{
		border: 1px solid var(--main-color) !important;
